import React, { useRef } from "react";

export default function CategorySection() {
  const scrollRef = useRef(null);

  const categories = [
    { name: "Must haves",           image: "/images/musthaves.jpg" },
    { name: "Vitamins & supplements", image: "/images/vitamins.jpg" },
    { name: "Monsoon store",        image: "/images/monsoon.jpg" },
    { name: "Personal care",        image: "/images/personalcare.jpg" },
    { name: "Ayurvedic care",       image: "/images/ayurveda.jpg" },
    { name: "Sports nutrition",     image: "/images/sports.jpg" },
    { name: "Heart care",           image: "/images/heart.jpg" },
    { name: "Diabetes essentials",  image: "/images/diabetes.jpg" },
  ];

  const scroll = (dir) => {
    scrollRef.current.scrollBy({
      left: dir === "left" ? -280 : 280,
      behavior: "smooth",
    });
  };

  return (
    <section className="category-strip container my-5">
      <h2 className="fw-bold mb-4">Shop by Categories</h2>

      <div className="strip-wrapper">
        <button className="strip-btn left" onClick={() => scroll("left")}>‹</button>

        <div className="strip-row" ref={scrollRef}>
          {categories.map((cat, i) => (
            <div key={i} className="strip-card text-center">
              <img src={cat.image} alt={cat.name} className="strip-img" />
              <p className="mt-2">{cat.name}</p>
            </div>
          ))}
        </div>

        <button className="strip-btn right" onClick={() => scroll("right")}>›</button>
      </div>
    </section>
  );
}
