import React, { useEffect, useState } from "react";
import api from "../api/axios";

function MedicineSearch() {
  const [query, setQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [expandedId, setExpandedId] = useState(null);

  useEffect(() => {
    const delayDebounce = setTimeout(() => {
      if (!query.trim()) return setSuggestions([]);

      const fetchData = async () => {
        try {
          const res = await api.get("/medicines/search", {
            params: { q: query },
          });
          setSuggestions(res.data);
        } catch (err) {
          console.error("Search error:", err.message);
        }
      };

      fetchData();
    }, 300);

    return () => clearTimeout(delayDebounce);
  }, [query]);

  const handleToggle = (id) => {
    setExpandedId(prev => (prev === id ? null : id));
  };

  return (
    <div style={{ maxWidth: "700px", margin: "0 auto", padding: "0 20px" }}>
      {/* ✅ Search Input */}
      <input
        type="text"
        placeholder="Search medicines..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        autoComplete="off"
        style={{
          width: "100%",
          padding: "10px 14px",
          fontSize: "16px",
          border: "1px solid #007bff",
          borderRadius: "6px",
          outline: "none",
        }}
      />

      {/* ✅ Compact Results */}
      {suggestions.length > 0 && (
        <div style={{ marginTop: "4px" }}>
          {suggestions.map((med) => (
            <div
              key={med._id}
              onClick={() => handleToggle(med._id)}
              style={{
                padding: "6px 0",
                cursor: "pointer",
                fontSize: "15px",
                color: "#007bff",
              }}
            >
              {med.name}

              {expandedId === med._id && (
                <div style={{ fontSize: "13px", color: "#444", marginTop: "4px" }}>
                  <div><strong>Category:</strong> {med.category}</div>
                  <div><strong>Symptoms:</strong> {med.symptoms.join(", ")}</div>
                  <div>{med.description}</div>
                  <div><strong>Price:</strong> ₹{med.price}</div>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MedicineSearch;
