import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import api from "../api/axios"; // ✅ Use your configured Axios instance

function Navbar({ scrolled }) {
  const [query, setQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    const fetchSuggestions = async () => {
      if (query.trim().length < 2) return setSuggestions([]);
      try {
        const res = await api.get("/medicines/search", {
          params: { q: query },
        });
        setSuggestions(res.data);
      } catch (err) {
        console.error("Search error:", err.message);
      }
    };

    const debounce = setTimeout(fetchSuggestions, 300);
    return () => clearTimeout(debounce);
  }, [query]);

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light shadow-sm fixed-top">
      <div className="container-fluid d-flex align-items-center justify-content-between">
        
        {/* Left: Logo + Search bar */}
        <div className="d-flex align-items-center flex-grow-1">
          <Link className="navbar-brand d-flex align-items-center me-3" to="/">
            <img
              src="/images/logo.png"
              alt="Logo"
              width="45"
              height="45"
              className="me-2"
            />
            <span className="fw-bold text-primary">
              Medi<span className="text-dark">Q</span>
            </span>
          </Link>

          {/* Search bar visible only when scrolled */}
          {scrolled && (
            <div className="search-bar-container compact position-relative">
              <input
                type="text"
                className="form-control me-2"
                placeholder="Search by name, symptom, or category"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
              />
              <button type="submit" className="btn btn-primary">
                Search
              </button>

              {/* Suggestions dropdown */}
              {suggestions.length > 0 && (
                <ul className="suggestion-list position-absolute bg-white border rounded mt-2 w-100">
                  {suggestions.map((med) => (
                    <li key={med._id} className="px-3 py-2 border-bottom">
                      <strong className="text-primary">{med.name}</strong>{" "}
                      <span className="text-muted">({med.category})</span>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          )}
        </div>

        {/* Right: Navigation links */}
        <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
          <ul className="navbar-nav align-items-center">
            <li className="nav-item">
              <Link className="nav-link" to="/">🏠 Home</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/medicines">💊 Shop</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/upload">📤 Upload</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/cart">🛒 Cart</Link>
            </li>
            <li className="nav-item">
              <Link className="btn btn-outline-primary ms-lg-3 mt-2 mt-lg-0 px-3" to="/login">
                🔑 Sign In / Join
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
