import React, { useEffect, useState } from "react";
import api from "../api/axios";
import "./Orders.css";

export default function Orders({ user }) {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchOrders = async () => {
      if (!token) return;

      try {
        const res = await api.get("/orders/my", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrders(res.data.orders || []);
      } catch (err) {
        console.error("‚ùå Failed to fetch orders:", err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, [token]); // ‚úÖ ESLint-compliant

  if (!user || !token) {
    return (
      <div className="container my-5">
        <h3 className="mb-4 text-primary">üì¶ My Orders</h3>
        <div className="alert alert-warning">Please log in to view your orders.</div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="container my-5 text-center">
        <h5 className="text-muted">Loading your orders...</h5>
      </div>
    );
  }

  return (
    <div className="container my-5">
      <h3 className="mb-4 text-primary">üì¶ My Orders</h3>
      {orders.length === 0 ? (
        <div className="alert alert-info">You haven‚Äôt placed any orders yet.</div>
      ) : (
        <div className="list-group">
          {orders.map((order) => (
            <div key={order._id} className="list-group-item mb-3">
              <h5>Order ID: {order._id}</h5>
              <p>Status: <strong>{order.status}</strong></p>
              <p>Total: ‚Çπ{order.totalAmount}</p>
              <ul className="mb-2">
                {order.medicines.map((med, idx) => (
                  <li key={idx}>
                    {med.medicineId?.name || "Unnamed"} √ó {med.quantity} ‚Äî ‚Çπ{med.price}
                  </li>
                ))}
              </ul>
              <small>Placed on: {new Date(order.placedAt).toLocaleString()}</small>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
