import { useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import api from "../api/axios"; // assumes you have axios instance setup
import { useUser } from "../context/UserContext"; // assumes user context provides userId

export default function BuyMedicine() {
  const { state } = useLocation();
  const medicine = state?.medicine;
  const { userId } = useUser(); // get logged-in user ID
  const [prescriptionFile, setPrescriptionFile] = useState(null);
  const [highlightUpload, setHighlightUpload] = useState(false);
  const [prescriptionStatus, setPrescriptionStatus] = useState(null); // null until fetched
  const [uploading, setUploading] = useState(false);

  if (!medicine) return <p className="text-center">No medicine selected.</p>;

  const isPrescriptionRequired = medicine.prescriptionRequired === "Required";

  // ‚úÖ Fetch prescription status from backend
  useEffect(() => {
    const fetchStatus = async () => {
      try {
        const { data } = await api.get(`/prescriptions/status`, {
          params: { userId, medicineId: medicine._id },
        });
        setPrescriptionStatus(data.status);
      } catch (err) {
        console.error("Failed to fetch prescription status:", err);
        setPrescriptionStatus(null);
      }
    };

    if (isPrescriptionRequired) {
      fetchStatus();
    }
  }, [medicine._id, userId, isPrescriptionRequired]);

  // ‚úÖ Upload prescription to backend
  const handleFileChange = async (e) => {
    const file = e.target.files?.[0];
    if (!file) return;

    setPrescriptionFile(file);
    setHighlightUpload(false);
    setUploading(true);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("userId", userId);
    formData.append("medicineId", medicine._id);

    try {
      await api.post("/prescriptions/upload", formData);
      setPrescriptionStatus("Pending");
    } catch (err) {
      console.error("Upload failed:", err);
      alert("Failed to upload prescription.");
    } finally {
      setUploading(false);
    }
  };

  const handleAddToCart = () => {
    if (isPrescriptionRequired && !prescriptionFile && !prescriptionStatus) {
      setHighlightUpload(true);
      alert("‚ö†Ô∏è Prescription is required to proceed.");
      return;
    }

    if (isPrescriptionRequired && prescriptionStatus !== "Approved") {
      alert(`‚õî Prescription status is "${prescriptionStatus}". You can proceed only after approval.`);
      return;
    }

    // Proceed to cart logic here
    console.log("‚úÖ Medicine added to cart:", medicine.name);
  };

  const imageUrl = medicine.image
    ? medicine.image.startsWith("/uploads")
      ? `http://localhost:5000${medicine.image}`
      : medicine.image
    : "/images/azivo.jpg";

  return (
    <div className="container my-5">
      <h2 className="mb-4 text-center text-success">üõí Buy Medicine</h2>

      <div className="card p-4 shadow-sm">
        <h4 className="text-center mb-3">{medicine.name}</h4>

        <div className="text-center mb-4">
          <img
            src={imageUrl}
            alt={medicine.name}
            style={{
              height: "180px",
              objectFit: "contain",
              borderRadius: "8px",
              border: "1px solid #211a1aff",
              backgroundColor: "#fff",
              maxWidth: "100%",
            }}
          />
        </div>

        <p><b>Category:</b> {medicine.category}</p>
        <p><b>Price:</b> ‚Çπ{medicine.price}</p>
        <p><b>Stock:</b> {medicine.stock}</p>
        <p><b>Dosage:</b> {medicine.dosage}</p>
        <p><b>Symptoms:</b> {medicine.symptoms?.join(", ")}</p>
        <p><b>Description:</b> {medicine.description}</p>
        <p><b>Prescription:</b> {medicine.prescriptionRequired}</p>

        {isPrescriptionRequired && (
          <div className="mt-4">
            <label className="form-label"><b>Upload Prescription</b></label>
            <input
              type="file"
              className={`form-control ${highlightUpload ? "border-danger" : ""}`}
              accept="image/*,.pdf"
              onChange={handleFileChange}
              disabled={uploading}
            />
            {prescriptionFile && (
              <p className="mt-2 text-success">
                ‚úÖ File selected: <i>{prescriptionFile.name}</i>
              </p>
            )}
            {highlightUpload && (
              <p className="text-danger mt-2">Prescription is required to proceed.</p>
            )}

            {/* ‚úÖ Prescription Status Display */}
            {prescriptionStatus && (
              <div className="mt-3">
                <label className="form-label"><b>Prescription Status</b></label>
                <div className={`alert alert-${prescriptionStatus === "Approved" ? "success" : prescriptionStatus === "Rejected" ? "danger" : "warning"}`}>
                  {prescriptionStatus}
                </div>
              </div>
            )}
          </div>
        )}

        <button
          className="btn btn-primary mt-4 w-100"
          onClick={handleAddToCart}
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
}
