import React, { useEffect, useState } from "react";
import api from "../api/axios";
import MedicineCard from "../components/MedicineCard"; // or whatever card component you use

export default function Ayurveda({ user }) {
  const [ayurvedicItems, setAyurvedicItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAyurveda = async () => {
      try {
        const res = await api.get("/medicines/category", {
          params: { type: "ayurveda" },
        });
        setAyurvedicItems(res.data || []);
      } catch (err) {
        console.error("‚ùå Ayurveda fetch error:", err.message);
        setAyurvedicItems([]);
      } finally {
        setLoading(false);
      }
    };

    fetchAyurveda();
  }, []);

  return (
    <div className="container my-5">
      <h2 className="mb-4 text-success">üåø Ayurvedic Medicines</h2>
      {loading ? (
        <p className="text-muted">Loading Ayurvedic products...</p>
      ) : ayurvedicItems.length === 0 ? (
        <div className="alert alert-info">No Ayurvedic medicines available.</div>
      ) : (
        <div className="row">
          {ayurvedicItems.map((item) => (
            <div key={item._id} className="col-md-4 mb-4">
              <MedicineCard medicine={item} user={user} />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
