// src/components/personalcare/PersonalCare.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './PersonalCare.css';

const categories = [
  'Skin Care',
  'Hair Care',
  'Baby and Mom Care',
  'Oral Care',
  'Elderly Care',
];

const products = [
  {
    name: 'Aloe Vera Gel',
    category: 'Skin Care',
    description: 'Hydrates and soothes skin.',
    price: 180,
    image: '/images/skincare/aloe-vera.jpg',
  },
  {
    name: 'Anti-Dandruff Shampoo',
    category: 'Hair Care',
    description: 'Fights flakes and itch.',
    price: 220,
    image: '/images/haircare/shampoo.jpg',
  },
  {
    name: 'Baby Lotion',
    category: 'Baby and Mom Care',
    description: 'Gentle moisturizer for babies.',
    price: 150,
    image: '/images/babymomcare/baby-lotion.jpg',
  },
  {
    name: 'Toothpaste',
    category: 'Oral Care',
    description: 'Herbal toothpaste for oral health.',
    price: 90,
    image: '/images/oralcare/toothpaste.jpg',
  },
  {
    name: 'Joint Pain Cream',
    category: 'Elderly Care',
    description: 'Relieves joint pain and inflammation.',
    price: 120,
    image: '/images/elderlycare/joint-pain.jpg',
  },
];

const PersonalCare = () => {
  const [selectedCategory, setSelectedCategory] = useState('Skin Care');
  const navigate = useNavigate();

  const handleCategoryClick = (cat) => {
    if (cat === 'Skin Care') {
      navigate('/personal-care/skin');
    } else if (cat === 'Hair Care') {
      navigate('/personal-care/hair');
    } else {
      setSelectedCategory(cat);
    }
  };

  const filteredProducts = products.filter(
    (product) => product.category === selectedCategory
  );

  return (
    <div className="personal-care-container container py-4">
      <h2 className="text-center mb-4">ðŸ§¼ Personal Care</h2>

      {/* Category Buttons */}
      <div className="d-flex flex-wrap justify-content-center mb-4">
        {categories.map((cat) => (
          <button
            key={cat}
            className={`btn m-2 ${
              selectedCategory === cat ? 'btn-primary' : 'btn-outline-primary'
            }`}
            onClick={() => handleCategoryClick(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Product Cards (only for non-Skin/Hair Care categories) */}
      {selectedCategory !== 'Skin Care' && selectedCategory !== 'Hair Care' && (
        <div className="row">
          {filteredProducts.map((product, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="card h-100 shadow-sm">
                <img
                  src={product.image}
                  alt={product.name}
                  className="card-img-top"
                  style={{ height: '200px', objectFit: 'cover' }}
                />
                <div className="card-body">
                  <h5 className="card-title text-primary">{product.name}</h5>
                  <p className="card-text text-muted">{product.description}</p>
                  <p className="fw-bold">â‚¹{product.price}</p>
                  <button className="btn btn-outline-success w-100">Add to Cart</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default PersonalCare;
