import React from "react";

function CategorySection() {
  const categories = [
    { name: "Medicines", image: "medicines.jpg" },
    { name: "Personal Care", image: "personalcare.jpg" },
    { name: "Health Care", image: "healthcare.jpg" },
    { name: "Vitamins", image: "vitamins.jpg" },
    { name: "Ayurveda", image: "ayurveda.jpg" },
    { name: "Derma Care", image: "dermacare.jpg" },
    { name: "Women", image: "women.jpg" },
    { name: "Mom & Baby", image: "momandbaby.jpg" },
    { name: "Health Blogs", image: "healthblogs.jpg" },
  ];

  return (
    <section className="category-section container my-5">
      {/* ✅ Section Title */}
      <h2 className="text-start mb-4 fw-bold">Shop by Categories</h2>

      {/* ✅ Cards Grid */}
      <div className="row g-4 text-center">
        {categories.map((cat, index) => (
          <div key={index} className="col-6 col-md-4 col-lg-2">
            <div className="category-card p-4 shadow-sm rounded h-100">
              <img
                src={`/images/categories/${cat.image}`}
                alt={cat.name}
                className="category-img img-fluid"
              />
              <h6 className="mt-3">{cat.name}</h6>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default CategorySection;
