import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

function Login({ setUser, setToken }) {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });

  // ✅ Redirect if already logged in
  useEffect(() => {
    const token = localStorage.getItem("token");
    const user = localStorage.getItem("user");

    if (token && user) {
      try {
        const parsedUser = JSON.parse(user);
        console.log("✅ Existing session detected:", parsedUser);

        switch (parsedUser?.role) {
          case "admin":
            navigate("/admin");
            break;
          case "pharmacist":
            navigate("/pharmacist");
            break;
          default:
            navigate("/");
        }
      } catch (err) {
        console.warn("⚠️ Failed to parse stored user:", err.message);
      }
    }
  }, [navigate]);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
  e.preventDefault(); // ✅ Prevent page reload

  // ✅ Force session save for debugging
  const fakeUser = {
    _id: "68df53e2cb6decde5b82cd51",
    userId: "U010",
    name: "devika",
    email: "devika@gmail.com",
    mobile: "6578543213",
    role: "user"
  };
  const fakeToken = "test-token-123";

  localStorage.setItem("user", JSON.stringify(fakeUser));
  localStorage.setItem("token", fakeToken);
  window.dispatchEvent(new Event("storage"));

  console.log("✅ Forced session save");
  console.log("📦 Stored user:", localStorage.getItem("user"));
  console.log("📦 Stored token:", localStorage.getItem("token"));

  if (setUser) setUser(fakeUser);
  if (setToken) setToken(fakeToken);

  alert("Login successful (forced session)");
  navigate("/");
};


  return (
    <div
      className="container d-flex justify-content-center align-items-center"
      style={{ minHeight: "80vh" }}
    >
      <div className="card shadow p-4 w-100" style={{ maxWidth: "400px" }}>
        <h2 className="text-center mb-4">Login</h2>

        <form onSubmit={handleSubmit} autoComplete="on">
          <input
            type="email"
            name="email"
            className="form-control mb-3"
            placeholder="Email Address"
            value={formData.email}
            onChange={handleChange}
            required
            autoComplete="email"
            autoFocus
            aria-label="Email address"
          />
          <input
            type="password"
            name="password"
            className="form-control mb-3"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
            autoComplete="current-password"
            aria-label="Password"
          />
          <button className="btn btn-primary w-100 mb-3">Login</button>
        </form>

        <p className="text-center">
          Don't have an account? <Link to="/register">Register here</Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
