import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/axios";

export default function PrescriptionStatus({ user }) {
  const [prescriptions, setPrescriptions] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (user?._id) {
      api.get(`/prescriptions/user/${user._id}`)
        .then(res => setPrescriptions(res.data))
        .catch(err => console.error("Failed to fetch prescriptions:", err));
    }
  }, [user]);

  if (!user) {
    return <p className="text-center text-danger">User not logged in.</p>;
  }

  return (
    <div className="container my-5">
      <h4 className="mb-4 text-primary">ðŸ§¾ Prescription Status</h4>
      {prescriptions.length === 0 ? (
        <div className="alert alert-info">No approved prescriptions found.</div>
      ) : (
        prescriptions.map(p => (
          <div key={p._id} className="card mb-4 p-3 shadow-sm">
            <h5 className="text-success">Prescription #{p._id.slice(-6)}</h5>
            <p><strong>Approved At:</strong> {new Date(p.updatedAt).toLocaleString()}</p>
            <ul className="list-group mb-3">
              Array.isArray(p.medicineIds) && p.medicineIds.map(med => (

                <li key={med._id} className="list-group-item d-flex justify-content-between align-items-center">
                  <div>
                    <strong>{med.name}</strong> â€“ â‚¹{med.price}
                    <br />
                    <small>{med.description}</small>
                  </div>
                  <button className="btn btn-sm btn-outline-primary" onClick={() => navigate(`/buy/${med._id}`)}>
                    Buy
                  </button>
                </li>
              ))}
            </ul>
            <button
              className="btn btn-success w-100"
              onClick={() => navigate(`/checkout?prescription=${p._id}`)}
            >
              ðŸ›’ Buy All Medicines
            </button>
          </div>
        ))
      )}
    </div>
  );
}
