import React, { useEffect, useState } from "react";
import api from "../api/axios";
import { Link } from "react-router-dom";
import "./Ayurveda.css";

export default function Ayurveda({ user }) {
  const [ayurvedicItems, setAyurvedicItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    const fetchAyurveda = async () => {
      try {
        const res = await api.get("/medicines/category", {
          params: { type: "ayurveda" },
        });
        setAyurvedicItems(res.data || []);
      } catch (err) {
        console.error("âŒ Ayurveda fetch error:", err.message);
        setAyurvedicItems([]);
      } finally {
        setLoading(false);
      }
    };

    fetchAyurveda();
  }, []);

  const filteredItems = ayurvedicItems.filter((item) => {
    if (filter === "all") return true;
    return item.name.toLowerCase().includes(filter);
  });

  return (
    <div className="container my-5">
      {/* ðŸŒ¿ Header */}
      <div className="ayurveda-header text-center mb-5">
        <h2 className="text-success">ðŸŒ¿ Ayurvedic Wellness</h2>
        <p className="text-muted">
          Explore traditional remedies rooted in Keralaâ€™s healing heritage.
        </p>
      </div>

      {/* ðŸ§­ Filter Options */}
      <div className="text-center mb-4">
        <button className={`btn btn-sm me-2 ${filter === "all" ? "btn-success" : "btn-outline-success"}`} onClick={() => setFilter("all")}>
          All
        </button>
        <button className={`btn btn-sm me-2 ${filter === "juice" ? "btn-success" : "btn-outline-success"}`} onClick={() => setFilter("juice")}>
          Juices
        </button>
        <button className={`btn btn-sm ${filter === "tablet" ? "btn-success" : "btn-outline-success"}`} onClick={() => setFilter("tablet")}>
          Tablets
        </button>
      </div>

      {/* ðŸ§ª Product Grid */}
      {loading ? (
        <div className="text-center text-muted">Loading Ayurvedic products...</div>
      ) : filteredItems.length === 0 ? (
        <div className="alert alert-info text-center">
          No Ayurvedic medicines available for this category.
        </div>
      ) : (
        <div className="row">
          {filteredItems.map((item) => (
            <div key={item._id} className="col-md-4 mb-4">
              <div className="card ayurveda-card h-100 shadow-sm">
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title text-success">{item.name}</h5>
                  <p className="card-text flex-grow-1">
                    {item.description || "Herbal remedy for wellness."}
                  </p>
                  <p className="fw-bold">â‚¹{item.price}</p>
                  <Link to={`/buy/${item._id}`} className="btn btn-outline-success mt-auto">
                    View & Buy
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
