import React, { useEffect, useState } from "react";
import api from "../api/axios";
import { Link } from "react-router-dom";
import "./Ayurveda.css";

// --- Mock Data for Testing ---
// ... (Mock Data remains the same)
const mockProducts = [
    { _id: "1", name: "Chyawanprash Special", price: "450.00", image: "/images/product-mock-1.png" },
    { _id: "2", name: "Ashwagandha Extract 60s", price: "299.00", image: "/images/product-mock-2.png" },
    { _id: "3", name: "Triphala Powder 250g", price: "125.50", image: "/images/product-mock-3.png" },
    { _id: "4", name: "Amla Juice 1L", price: "180.00", image: "/images/product-mock-4.png" },
    { _id: "5", name: "Brahmi Vati", price: "320.00", image: "/images/product-mock-5.png" },
    { _id: "6", name: "Giloy Capsules", price: "210.00", image: "/images/product-mock-6.png" },
    { _id: "7", name: "Turmeric Tablet", price: "150.00", image: "/images/product-mock-7.png" },
    { _id: "8", name: "Neem Face Pack", price: "95.00", image: "/images/product-mock-8.png" },
    { _id: "9", name: "Shatavari Granules", price: "410.00", image: "/images/product-mock-9.png" },
    { _id: "10", name: "Aloe Vera Juice 500ml", price: "145.00", image: "/images/product-mock-10.png" },
    { _id: "11", name: "Chyawanprash Special", price: "450.00", image: "/images/product-mock-1.png" },
    { _id: "12", name: "Ashwagandha Extract 60s", price: "299.00", image: "/images/product-mock-2.png" },
    { _id: "13", name: "Triphala Powder 250g", price: "125.50", image: "/images/product-mock-3.png" },
    { _id: "14", name: "Guduchi Juice", price: "220.00", image: "/images/product-mock-4.png" },
];
// --- End Mock Data ---


// --- FooterSection Component Definition (Shortened for space) ---
function FooterSection() {
    const FooterLink = ({ to, children }) => (
        <Link to={to} className="footer-link-text text-muted text-decoration-none">
            {children}
        </Link>
    );
    return (
        <section className="bg-light border-top pt-5">
            <div className="container text-center">
                {/* Metrics, Highlights, etc. omitted for brevity */}
                <div className="row text-start py-5 border-top">
                    {/* Footer Links columns omitted for brevity */}
                    <div className="col-md-3 mb-4">
                        <h6 className="fw-bold">Connect</h6>
                        <div className="d-flex gap-3 mb-2">
                            <a href="https://facebook.com" target="_blank" rel="noreferrer" className="social-icon"><i className="bi bi-facebook fs-4"></i></a>
                            <a href="https://instagram.com" target="_blank" rel="noreferrer" className="social-icon"><i className="bi bi-instagram fs-4"></i></a>
                        </div>
                        <form>
                            <label className="form-label text-muted">Want daily dose of health?</label>
                            <input type="email" className="form-control mb-2" placeholder="Enter your email" />
                            <button type="submit" className="btn btn-success w-100">SIGN UP</button>
                        </form>
                    </div>
                </div>
            </div>
        </section>
    );
}
// --- End of FooterSection Component Definition ---


export default function Ayurveda() {
    const [products, setProducts] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState(''); 

    useEffect(() => {
        const fetchAyurvedicProducts = async () => {
            try {
                const res = await api.get("/medicines/category", {
                    params: { type: "ayurveda" },
                });
                const fetchedProducts = Array.isArray(res.data) && res.data.length > 0
                    ? res.data
                    : mockProducts;
                
                setProducts(fetchedProducts);

            } catch (err) {
                console.error("‚ùå Error fetching Ayurveda products, using mock data:", err.message);
                setProducts(mockProducts);
            } finally {
                setIsLoading(false);
            }
        };

        fetchAyurvedicProducts();
    }, []);

    const renderCarouselCard = (item) => (
        <div key={item._id} className="carousel-card">
            <img
                src={item.image || "/images/default-product.png"}
                alt={item.name}
                className="carousel-image"
            />
            <h5 className="carousel-name">{item.name}</h5>
            <p className="carousel-price">‚Çπ{item.price} <span className="badge text-bg-danger">15% OFF</span></p>
            <button className="btn btn-outline-success btn-sm">Add +</button>
        </div>
    );
    
    const filteredProducts = products.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (isLoading) {
        return (
            <div className="ayurveda-store text-center p-5">
                <div className="spinner-border text-success" role="status">
                    <span className="visually-hidden">Loading...</span>
                </div>
                <p className="mt-2">Loading Ayurvedic treasures...</p>
            </div>
        );
    }

    return (
        <div className="ayurveda-store">
            {/* üåø Banner with Integrated Search */}
            <div
                className="banner"
                style={{
                    backgroundImage: 'url("/images/ayurveda-banner.jpg")',
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                    height: '300px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    flexDirection: 'column',
                    textAlign: 'center',
                    color: '#2e7d32',
                    position: 'relative', // Added for search bar positioning
                    overflow: 'hidden' // Important for visual containment
                }}
            >
                {/* Text Content */}
                <div className="banner-content">
                    <h1>MediQ Ayurvedic Store</h1>
                    <p>Rooted in Kerala‚Äôs healing traditions</p>
                </div>

                {/* üîç Search Bar Overlay */}
                <div className="banner-search-container">
                    <form className="d-flex">
                        <input
                            type="search"
                            className="form-control form-control-lg me-2"
                            placeholder="Search for Ayurvedic products, herbs, or brands..."
                            aria-label="Search Ayurveda"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                        <button type="submit" className="btn btn-lg btn-success">
                            <i className="bi bi-search"></i>
                        </button>
                    </form>
                </div>
            </div>
            {/* End Banner */}

            {/* Conditional Product Display (Carousels vs. Search Results Grid) */}
            {searchTerm ? (
                // üîé Display Search Results in a simple grid
                <section className="section search-results">
                    <h2 className="section-title">Search Results</h2>
                    <p className="mt-3 text-muted w-100 text-center">Showing {filteredProducts.length} results for **"{searchTerm}"**</p>
                    <div className="product-grid">
                        {filteredProducts.length > 0 ? (
                            filteredProducts.map((item) => renderCarouselCard(item))
                        ) : (
                            <p className="text-muted w-100 text-center">No products found matching "{searchTerm}".</p>
                        )}
                    </div>
                </section>
            ) : (
                // üèÜ Display Standard Carousels
                <>
                    <section className="carousel-section">
                        <h2 className="section-title">Top Ayurvedic Treasures</h2>
                        <div className="carousel-container">
                            {products.slice(0, 10).map((item) => renderCarouselCard(item))}
                        </div>
                    </section>

                    <section className="carousel-section specialty">
                        <h2 className="section-title">Specialty Ayurvedic Picks</h2>
                        <div className="carousel-container">
                            {products.slice(10, 17).map((item) => renderCarouselCard(item))}
                        </div>
                    </section>

                    <section className="carousel-section juices">
                        <h2 className="section-title">Juices for Wellness</h2>
                        <div className="carousel-container">
                            {products
                                .filter((p) => p.name && p.name.toLowerCase().includes("juice"))
                                .slice(0, 10)
                                .map((item) => renderCarouselCard(item))}
                        </div>
                    </section>
                </>
            )}

            {/* üß≠ Shop by Category */}
            <section className="section categories">
                <h2 className="section-title">Shop by Category</h2>
                <div className="category-grid">
                    {["ayurveda", "homeopathy", "unani", "siddha"].map((cat) => (
                        <div key={cat} className="category-tile">
                            <img
                                src={`/images/${cat}.png`}
                                alt={cat}
                                className="category-image"
                            />
                            <h5>{cat.charAt(0).toUpperCase() + cat.slice(1)}</h5>
                        </div>
                    ))}
                </div>
            </section>

            {/* üìö Blog Previews */}
            <section className="section blog">
                <h2 className="section-title">Ayurvedic Home Remedies</h2>
                <div className="blog-grid">
                    {["Amla Benefits", "Stress Relief", "Herbal Cleansing", "Skin Tips"].map((title, idx) => (
                        <div key={idx} className="blog-card">
                            <img
                                src={`/images/blog${idx + 1}.jpg`}
                                alt={title}
                                className="blog-image"
                            />
                            <h5>{title}</h5>
                            <p>Explore traditional remedies and wellness tips.</p>
                            <Link to="/blog" className="btn btn-sm btn-outline-success">
                                Read More
                            </Link>
                        </div>
                    ))}
                </div>
            </section>

            {/* Footer */}
            <FooterSection />
        </div>
    );
}
