import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/axios";

export default function Medicines() {
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(false);
  const [medicines, setMedicines] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMedicines = async () => {
      setLoading(true);
      try {
        const url = query.trim()
          ? `/medicines/search?q=${encodeURIComponent(query)}`
          : `/medicines`;
        const { data } = await api.get(url);
        setMedicines(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error("Error fetching medicines:", error);
        setMedicines([]);
      } finally {
        setLoading(false);
      }
    };

    const debounce = setTimeout(fetchMedicines, 300);
    return () => clearTimeout(debounce);
  }, [query]);

  return (
    <div className="container my-5">
      <h2 className="mb-4 text-center text-primary">🩺 Browse Medicines</h2>

      {/* Search Bar */}
      <div className="input-group mb-4">
        <input
          className="form-control"
          placeholder="Search by name, category, or symptoms..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>

      {/* Loading State */}
      {loading && <p className="text-center">Loading…</p>}

      {/* Empty State */}
      {!loading && medicines.length === 0 && (
        <p className="text-center text-muted">No medicines found.</p>
      )}

      {/* Medicine List */}
      <div className="list-group">
        {medicines.map((med) => (
          <div key={med._id} className="list-group-item">
            {/* Collapsed View */}
            <div
              className="d-flex justify-content-between align-items-center"
              style={{ cursor: "pointer" }}
              onClick={() =>
                setSelectedId(selectedId === med._id ? null : med._id)
              }
            >
              <div>
                <strong>{med.name}</strong>
                <div className="text-muted small">
                  {med.symptoms?.[0] || med.category}
                </div>
              </div>
              <span className="text-primary">
                {selectedId === med._id ? "▲" : "▼"}
              </span>
            </div>

            {/* Expanded View */}
            {selectedId === med._id && (
              <div className="mt-3 border-top pt-3">
                {med.image && (
                  <img
                    src={med.image}
                    alt={med.name}
                    style={{
                      height: "160px",
                      objectFit: "contain",
                      backgroundColor: "#fff",
                      border: "1px solid #eee",
                      borderRadius: "8px",
                      marginBottom: "12px",
                      maxWidth: "100%",
                    }}
                  />
                )}
                <p><b>Category:</b> {med.category}</p>
                <p><b>Price:</b> ₹{med.price}</p>
                <p><b>Stock:</b> {med.stock}</p>
                {med.symptoms?.length > 0 && (
                  <p><b>Symptoms:</b> {med.symptoms.join(", ")}</p>
                )}
                {med.dosage && <p><b>Dosage:</b> {med.dosage}</p>}
                {med.description && <p><b>Description:</b> {med.description}</p>}
                <p><b>Prescription:</b> {med.prescriptionRequired ? "Required" : "Not Required"}</p>

                {/* ✅ Navigate to Buy Page */}
                <button
                  className="btn btn-success w-100 mt-2"
                  onClick={() => navigate("/buy", { state: { medicine: med } })}
                >
                  <i className="bi bi-cart-plus me-2"></i>Add to Cart
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
