// src/components/personalcare/ElderlyCare.jsx
import React from 'react';
import './ElderlyCare.css';

const products = [
  {
    name: 'Joint Pain Cream',
    description: 'Relieves joint pain and inflammation. Ayurvedic formula.',
    price: 120,
    image: '/images/elderly/joint-pain-cream.jpg',
  },
  {
    name: 'Calcium Tablets',
    description: 'Supports bone strength and mobility.',
    price: 200,
    image: '/images/elderly/calcium-tablets.jpg',
  },
  {
    name: 'Adult Diapers',
    description: 'Comfortable and leak-proof. Pack of 10.',
    price: 450,
    image: '/images/elderly/adult-diapers.jpg',
  },
];

const ElderlyCare = () => {
  return (
    <div className="elderly-care-container container py-4">
      <h2 className="text-center mb-4">ðŸ§“ Elderly Care Products</h2>
      <div className="row">
        {products.map((product, index) => (
          <div key={index} className="col-md-4 mb-4">
            <div className="card h-100 shadow-sm">
              <img
                src={product.image}
                alt={product.name}
                className="card-img-top"
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <div className="card-body">
                <h5 className="card-title text-success">{product.name}</h5>
                <p className="card-text text-muted">{product.description}</p>
                <p className="fw-bold">â‚¹{product.price}</p>
                <button className="btn btn-outline-success w-100">Add to Cart</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ElderlyCare;
