import React from 'react';
import './HairCare.css';

const hairCareProducts = [
  {
    name: 'Anti-Dandruff Shampoo',
    description: 'Fights dandruff and soothes scalp irritation. Suitable for all hair types.',
    price: 220,
    image: '/images/hair/anti-dandruff-shampoo.jpg',
  },
  {
    name: 'Hair Growth Oil',
    description: 'Promotes hair growth and reduces hair fall. Enriched with natural herbs.',
    price: 350,
    image: '/images/hair/hair-growth-oil.jpg',
  },
  {
    name: 'Hair Serum',
    description: 'Smoothens frizz and adds shine. Ideal for styling and protection.',
    price: 300,
    image: '/images/hair/hair-serum.jpg',
  },
  {
    name: 'Herbal Conditioner',
    description: 'Nourishes and strengthens hair. Free from parabens and sulfates.',
    price: 180,
    image: '/images/hair/herbal-conditioner.jpg',
  },
  {
    name: 'Dry Shampoo Spray',
    description: 'Instant refresh for oily hair. No water needed.',
    price: 250,
    image: '/images/hair/dry-shampoo.jpg',
  },
];

const HairCare = () => {
  return (
    <div className="hair-care-container container py-4">
      <h2 className="text-center mb-4">ðŸ’‡ Hair Care Products</h2>
      <div className="row">
        {hairCareProducts.map((product, index) => (
          <div key={index} className="col-md-4 mb-4">
            <div className="card h-100 shadow-sm">
              <img
                src={product.image}
                alt={product.name}
                className="card-img-top"
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <div className="card-body">
                <h5 className="card-title text-primary">{product.name}</h5>
                <p className="card-text text-muted">{product.description}</p>
                <p className="fw-bold">â‚¹{product.price}</p>
                <button className="btn btn-outline-success w-100">Add to Cart</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HairCare;
