import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/axios";

export default function VitaminsPage() {
  const [products, setProducts] = useState([]);
  const [query, setQuery] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    api
      .get("/medicines?category=Vitamins")
      .then((res) => setProducts(res.data || []))
      .catch((err) => console.error("Failed to fetch vitamins:", err));
  }, []);

  const filtered = products.filter(p =>
    p.name.toLowerCase().includes(query.toLowerCase()) ||
    p.symptoms?.some(s => s.toLowerCase().includes(query.toLowerCase()))
  );

  return (
    <div className="container my-5">
      <h3 className="text-primary mb-4">ðŸ§ª Vitamins & Supplements</h3>

      <input
        className="form-control mb-4"
        placeholder="Search by name or symptom..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />

      {filtered.length === 0 ? (
        <div className="alert alert-info">No products found.</div>
      ) : (
        <div className="row">
          {filtered.map((p) => (
            <div key={p._id} className="col-md-4 col-sm-6 mb-4">
              <div className="card h-100 shadow-sm">
                {p.imageUrl ? (
                  <img
                    src={p.imageUrl}
                    alt={p.name}
                    className="card-img-top"
                    style={{ height: "200px", objectFit: "cover" }}
                  />
                ) : (
                  <div
                    className="bg-light d-flex align-items-center justify-content-center"
                    style={{ height: "200px" }}
                  >
                    <span className="text-muted">No Image</span>
                  </div>
                )}
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title">{p.name}</h5>
                  <p className="card-text text-muted" style={{ minHeight: "60px" }}>
                    {p.description || "No description available."}
                  </p>
                  <p className="mt-auto mb-2"><strong>â‚¹{p.price}</strong></p>
                  <button
                    className="btn btn-outline-primary w-100"
                    onClick={() => navigate(`/buy/${p._id}`)}
                  >
                    Buy Now
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
