import React, { useEffect } from "react";

function CategorySection() {
  const categories = [
    { name: "Medicines", image: "/images/medicines.jpg" },
    { name: "Personal Care", image: "/images/personalcare.jpg" },
    { name: "Health Care", image: "/images/healthcare.jpg" },
    { name: "Vitamins", image: "/images/vitamins.jpg" },
    { name: "Ayurveda", image: "/images/ayurveda.jpg" },
    { name: "Derma Care", image: "/images/dermacare.jpg" },
    { name: "Women", image: "/images/women.jpg" },
    { name: "Mom & Baby", image: "/images/momandbaby.jpg" },
    
  ];

  // Group categories into sets of 5 per slide
  const chunkedCategories = [];
  for (let i = 0; i < categories.length; i += 5) {
    chunkedCategories.push(categories.slice(i, i + 5));
  }

  useEffect(() => {
    const bootstrap = require("bootstrap/dist/js/bootstrap.bundle.min.js");
    const carouselElement = document.querySelector("#categoryCarousel");
    if (carouselElement) {
      new bootstrap.Carousel(carouselElement, {
        interval: 3000, // auto slide
        ride: "carousel",
        pause: false,
        wrap: true, // ✅ infinite loop
      });
    }
  }, []);

  return (
    <section className="category-section container my-5">
      <h2 className="text-center mb-4 fw-bold">Shop by Categories</h2>

      <div id="categoryCarousel" className="carousel slide">
        <div className="carousel-inner">
          {chunkedCategories.map((group, index) => (
            <div
              key={index}
              className={`carousel-item ${index === 0 ? "active" : ""}`}
            >
              <div className="d-flex justify-content-center gap-4 flex-wrap">
                {group.map((cat, i) => (
                  <div key={i} className="category-card text-center">
                    <img src={cat.image} alt={cat.name} className="category-img" />
                    <p className="mt-2 mb-0 fw-semibold">{cat.name}</p>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        {/* controls */}
        <button
          className="carousel-control-prev"
          type="button"
          data-bs-target="#categoryCarousel"
          data-bs-slide="prev"
        >
          <span className="carousel-control-prev-icon"></span>
        </button>
        <button
          className="carousel-control-next"
          type="button"
          data-bs-target="#categoryCarousel"
          data-bs-slide="next"
        >
          <span className="carousel-control-next-icon"></span>
        </button>
      </div>
    </section>
  );
}

export default CategorySection;
