import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import api from "../api/axios";

function Login({ setUser, setToken }) {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // ‚úÖ Redirect if already logged in
  useEffect(() => {
    const token = localStorage.getItem("token");
    const user = localStorage.getItem("user");

    if (token && user) {
      try {
        const parsedUser = JSON.parse(user);
        console.log("‚úÖ Existing session detected:", parsedUser);

        switch (parsedUser?.role) {
          case "admin":
            navigate("/admin");
            break;
          case "pharmacist":
            navigate("/pharmacist");
            break;
          default:
            navigate("/");
        }
      } catch (err) {
        console.warn("‚ö†Ô∏è Failed to parse stored user:", err.message);
      }
    }
  }, [navigate]);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    const email = formData.email.trim().toLowerCase();
    const password = formData.password.trim();

    if (!email.includes("@") || !email.includes(".")) {
      setError("Please enter a valid email address.");
      setLoading(false);
      return;
    }

    try {
      const res = await api.post("/auth/login", { email, password });
      console.log("üîç Raw login response:", res.data);

      const user = res.data?.user;
      const token = res.data?.token;

      if (
  user &&
  typeof user === "object" &&
  user._id &&
  typeof token === "string" &&
  token.length >= 10
) {
  localStorage.setItem("user", JSON.stringify(user));
  localStorage.setItem("token", token);
  window.dispatchEvent(new Event("storage")); // triggers App.jsx sync

  console.log("‚úÖ Session saved:", user, token);

  if (setUser) setUser(user);
  if (setToken) setToken(token);

  alert(res.data.message || "Login successful");
  window.scrollTo({ top: 0, behavior: "smooth" });

  switch (user.role) {
    case "pharmacist":
      navigate("/pharmacist");
      break;
    case "admin":
      navigate("/admin");
      break;
    default:
      navigate("/");
  }
} else {
  console.error("‚ùå Login response missing user or token:", res.data);
  setError("Login failed: missing user info or token.");
}


      if (
        !user ||
        typeof user !== "object" ||
        !user._id ||
        !user.role ||
        typeof token !== "string" ||
        token.length < 10
      ) {
        console.error("‚ùå Invalid login response:", res.data);
        setError("Login failed: missing user info or token.");
        setLoading(false);
        return;
      }

      // ‚úÖ Store session in localStorage
      localStorage.setItem("user", JSON.stringify(user));
      localStorage.setItem("token", token);
      window.dispatchEvent(new Event("storage")); // triggers App.jsx sync

      console.log("üì¶ Stored user:", localStorage.getItem("user"));
      console.log("üì¶ Stored token:", localStorage.getItem("token"));

      // ‚úÖ Sync global state
      if (setUser) setUser(user);
      if (setToken) setToken(token);

      alert(res.data.message || "Login successful");
      window.scrollTo({ top: 0, behavior: "smooth" });

      switch (user.role) {
        case "pharmacist":
          navigate("/pharmacist");
          break;
        case "admin":
          navigate("/admin");
          break;
        default:
          navigate("/");
      }
    } catch (err) {
      console.error("‚ùå Login error:", err);
      setError(err.response?.data?.message || "Login failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="container d-flex justify-content-center align-items-center"
      style={{ minHeight: "80vh" }}
    >
      <div className="card shadow p-4 w-100" style={{ maxWidth: "400px" }}>
        <h2 className="text-center mb-4">Login</h2>

        {error && (
          <div className="alert alert-danger text-center py-2">{error}</div>
        )}

        <form onSubmit={handleSubmit} autoComplete="on">
          <input
            type="email"
            name="email"
            className="form-control mb-3"
            placeholder="Email Address"
            value={formData.email}
            onChange={handleChange}
            required
            autoComplete="email"
            autoFocus
            aria-label="Email address"
          />
          <input
            type="password"
            name="password"
            className="form-control mb-3"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
            autoComplete="current-password"
            aria-label="Password"
          />
          <button className="btn btn-primary w-100 mb-3" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>

        <p className="text-center">
          Don't have an account? <Link to="/register">Register here</Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
