import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import MedicineSearch from "../components/MedicineSearch";

function Navbar() {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 120) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light shadow-sm fixed-top">
      <div className="container-fluid">
        {/* Logo */}
        <Link className="navbar-brand d-flex align-items-center" to="/">
          <img
            src="/images/logo.png"
            alt="Logo"
            width="45"
            height="45"
            className="me-2"
          />
          <span className="fw-bold text-primary">
            Medi<span className="text-dark">Q</span>
          </span>
        </Link>

        {/* Toggler (mobile) */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Links */}
        <div className="collapse navbar-collapse" id="navbarNav">
          {/* Left links */}
          <ul className="navbar-nav mx-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/">🏠 Home</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/medicines">💊 Shop</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/upload">📤 Upload</Link>
            </li>
          </ul>

          {/* Right actions */}
          <ul className="navbar-nav ms-auto align-items-center">
            {/* Search inside navbar */}
            <li className="nav-item d-none d-lg-block me-3">
              <div className={scrolled ? "compact-search" : "expanded-search"}>
                <MedicineSearch />
              </div>
            </li>

            <li className="nav-item">
              <Link className="nav-link" to="/cart">🛒 Cart</Link>
            </li>
            <li className="nav-item">
              <Link className="btn btn-outline-primary ms-lg-3 mt-2 mt-lg-0 px-3" to="/login">
                🔑 Sign In / Join
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
