import React, { useEffect, useState } from "react";
import MedicineSearch from "../components/MedicineSearch";
import { Link } from "react-router-dom";
import CategorySection from "../components/CategorySection";
import CategoryNavbar from "../components/CategoryNavbar";
import PersonalCare from "../components/personalcare/PersonalCare";
import api from "../api/axios";
import "bootstrap/dist/js/bootstrap.bundle";

function Home() {
  const [scrolled, setScrolled] = useState(false);
  const [query, setQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("Medicines");
  const user = JSON.parse(localStorage.getItem("user"));

  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 150);
    window.addEventListener("scroll", handleScroll);

    const carouselElement = document.querySelector("#heroCarousel");
    if (carouselElement) {
      const bootstrap = require("bootstrap/dist/js/bootstrap.bundle.min.js");
      new bootstrap.Carousel(carouselElement, {
        interval: 3000,
        ride: "carousel",
        pause: false,
      });
    }

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    const fetchSuggestions = async () => {
      if (query.trim().length < 2) return setSuggestions([]);
      try {
        const res = await api.get("/medicines/search", { params: { q: query } });
        setSuggestions(res.data);
      } catch (err) {
        console.error("Search error:", err.message);
      }
    };

    const debounce = setTimeout(fetchSuggestions, 300);
    return () => clearTimeout(debounce);
  }, [query]);

  const renderCategorySection = () => {
    switch (selectedCategory) {
      case "Personal Care":
        return <PersonalCare />;
      case "Medicines":
      default:
        return <CategorySection />;
    }
  };

  return (
    <div>
      {/* âœ… HERO SECTION */}
      <div style={{ position: "relative" }}>
        <div
          id="heroCarousel"
          className="carousel slide carousel-fade"
          style={{ margin: "20px", borderRadius: "12px", overflow: "hidden" }}
        >
          <div className="carousel-indicators">
            <button type="button" data-bs-target="#heroCarousel" data-bs-slide-to="0" className="active"></button>
            <button type="button" data-bs-target="#heroCarousel" data-bs-slide-to="1"></button>
            <button type="button" data-bs-target="#heroCarousel" data-bs-slide-to="2"></button>
          </div>

          <div className="carousel-inner">
            <div className="carousel-item active hero-slide" style={carouselStyle("/images/bg1.jpg")}>
              <CarouselContent />
            </div>
            <div className="carousel-item hero-slide" style={carouselStyle("/images/bg2.jpg")}>
              <CarouselContent />
            </div>
            <div className="carousel-item hero-slide" style={carouselStyle("/images/bg3.jpg")}>
              <CarouselContent />
            </div>
          </div>

          <button className="carousel-control-prev" type="button" data-bs-target="#heroCarousel" data-bs-slide="prev">
            <span className="carousel-control-prev-icon"></span>
          </button>
          <button className="carousel-control-next" type="button" data-bs-target="#heroCarousel" data-bs-slide="next">
            <span className="carousel-control-next-icon"></span>
          </button>
        </div>

        {/* âœ… OVERLAY SEARCH BAR */}
        <div className="overlay-search-bar">
          <div className="welcome-text">
            <h1 className="fw-bold mb-3">
              Welcome to <span className="text-info">MediQ</span>
            </h1>
            <p className="text-muted mb-4">
              Your trusted pharmacy partner â€“ search, order & manage medicines online.
            </p>
          </div>
          <MedicineSearch query={query} setQuery={setQuery} scrolled={scrolled} />
        </div>
      </div>

      {/* âœ… Search Results BELOW Hero */}
      {suggestions.length > 0 && (
        <div className="container mt-4 p-4 bg-white rounded shadow">
          <h5 className="mb-3 text-primary">
            Showing results for: <span className="text-dark">{query}</span>
          </h5>
          <div className="row">
            {suggestions.map((med) => (
              <div key={med._id} className="col-md-4 mb-4">
                <div className="p-3 border rounded shadow-sm h-100 bg-light">
                  <h6 className="text-primary">{med.name}</h6>
                  <p><strong>Category:</strong> {med.category}</p>
                  <p><strong>Symptoms:</strong> {med.symptoms.join(", ")}</p>
                  <p className="text-muted" style={{ fontSize: "14px" }}>{med.description}</p>
                  <p><strong>Price:</strong> â‚¹{med.price}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* âœ… CATEGORY NAVBAR */}
      <CategoryNavbar onSelect={setSelectedCategory} />

      {/* âœ… DYNAMIC CATEGORY SECTION */}
      {renderCategorySection()}

      {/* âœ… Feature Cards */}
      <section className="container my-5">
        <div className="row text-center g-4">
          <div className="col-md-4">
            <div className="card shadow h-100">
              <div className="card-body">
                <h3>ðŸ”Ž Search Medicines</h3>
                <p>Find medicines by name, category, or symptoms easily.</p>
              </div>
            </div>
          </div>
          <div className="col-md-4">
            <Link to="/upload" className="card shadow h-100 text-decoration-none text-dark">
              <div className="card-body">
                <h3>ðŸ“¤ Upload Prescription</h3>
                <p>Upload doctorâ€™s prescription for pharmacist approval.</p>
              </div>
            </Link>
          </div>
          <div className="col-md-4">
            <div className="card shadow h-100">
              <div className="card-body">
                <h3>ðŸ›’ Easy Orders</h3>
                <p>Buy approved medicines in one click or select only what you need.</p>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* âœ… Quick Links */}
      <section className="bg-light py-4">
        <div className="container text-center">
          <Link to="/search" className="btn btn-outline-primary m-2">Browse Medicines</Link>
          {user?.role !== "pharmacist" && (
            <>
              <Link to="/upload" className="btn btn-outline-success m-2">Upload Prescription</Link>
              <Link to="/orders" className="btn btn-outline-dark m-2">View Orders</Link>
              <Link to="/profile" className="btn btn-outline-secondary m-2">ðŸ‘¤ My Profile</Link>
            </>
          )}
          {user?.role === "pharmacist" && (
            <Link to="/pharmacist" className="btn btn-outline-info m-2">Pharmacist Dashboard</Link>
          )}
        </div>
      </section>

      {/* âœ… Platform Highlights */}
      <section className="bg-light py-5 border-top">
        <div className="container text-center">
          <h3 className="mb-4 text-primary">We Bring Care to Health</h3>
          <div className="row">
            <div className="col-md-4 mb-3">
              <h4 className="text-success">260M+</h4>
              <p className="text-muted">Visitors across India</p>
            </div>
            <div className="col-md-4 mb-3">
              <h4 className="text-success">31M+</h4>
              <p className="text-muted">Orders Delivered</p>
            </div>
            <div className="col-md-4 mb-3">
              <h4 className="text-success">1800+</h4>
              <p className="text-muted">Cities Covered</p>
            </div>
          </div>
        </div>
      </section>

            {/* âœ… Platform Values + App Download */}
      <section className="bg-white py-5 border-top">
        <div className="container">
          <div className="row text-center mb-4">
            <div className="col-md-4">
              <i className="bi bi-shield-check text-primary fs-2"></i>
              <h5 className="mt-2">Secure</h5>
              <p className="text-muted">
                Your data is safe with us. Our systems are ISO/IEC 27001:2013 certified.
              </p>
            </div>
            <div className="col-md-4">
              <i className="bi bi-currency-rupee text-primary fs-2"></i>
              <h5 className="mt-2">Affordable</h5>
              <p className="text-muted">
                Get maximum savings on every order with verified discounts and offers.
              </p>
            </div>
            <div className="col-md-4">
              <i className="bi bi-check-circle text-primary fs-2"></i>
              <h5 className="mt-2">Reliable</h5>
              <p className="text-muted">
                All products are curated by our expert team and delivered with care.
              </p>
            </div>
          </div>

          <div className="text-center">
            <h5 className="mb-3">Download Our App</h5>
            <div className="d-flex justify-content-center gap-3 flex-wrap">
              <a
                href="https://play.google.com/store"
                target="_blank"
                rel="noopener noreferrer"
              >
                <img
                  src="/assets/google-play-badge.png"
                  alt="Google Play"
                  height="50"
                />
              </a>
              <a
                href="https://www.apple.com/in/app-store/"
                target="_blank"
                rel="noopener noreferrer"
              >
                <img
                  src="/assets/app-store-badge.png"
                  alt="App Store"
                  height="50"
                />
              </a>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

function carouselStyle(imageUrl) {
  return {
    backgroundImage: `url('${imageUrl}')`,
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    height: "70vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center",
  };
}

function CarouselContent() {
  return (
    <div
      style={{
        position: "relative",
        maxWidth: "700px",
        width: "100%",
        padding: "20px",
        color: "#333",
      }}
    >
      <h1 className="fw-bold mb-3">
        Welcome to <span className="text-info">MediQ</span>
      </h1>
      <p className="text-muted mb-4">
        Your trusted pharmacy partner â€“ search, order & manage medicines online.
      </p>
    </div>
  );
}

export default Home;
