import React from 'react';
import { useNavigate } from 'react-router-dom';
import './PersonalCare.css';

const categories = [
  'Skin Care',
  'Hair Care',
  'Baby and Mom Care',
  'Oral Care',
  'Elderly Care',
];

const products = [
  { name: 'Aloe Vera Gel', category: 'Skin Care', price: 180, image: '/images/skincare/aloe-vera.jpg' },
  { name: 'Anti-Dandruff Shampoo', category: 'Hair Care', price: 220, image: '/images/haircare/shampoo.jpg' },
  { name: 'Baby Lotion', category: 'Baby and Mom Care', price: 150, image: '/images/babymomcare/baby-lotion.jpg' },
  { name: 'Herbal Toothpaste', category: 'Oral Care', price: 90, image: '/images/oralcare/toothpaste.jpg' },
  { name: 'Joint Pain Cream', category: 'Elderly Care', price: 120, image: '/images/elderlycare/joint-pain.jpg' },
];

const PersonalCare = () => {
  const navigate = useNavigate();

  const handleCategoryClick = (cat) => {
    const routeMap = {
      'Skin Care': '/personal-care/skin',
      'Hair Care': '/personal-care/hair',
      'Baby and Mom Care': '/personal-care/baby-mom',
      'Oral Care': '/personal-care/oral',
      'Elderly Care': '/personal-care/elderly',
    };
    navigate(routeMap[cat]);
  };

  const groupedProducts = categories.map((category) => ({
    category,
    items: products.filter((p) => p.category === category)
  }));

  return (
    <div className="personal-care-container container py-4">
      {/* Hero Section */}
      <div className="hero-section text-center mb-5">
        <h1 className="display-5 fw-bold text-primary">ðŸ§¼ Personal Care</h1>
        <p className="lead text-muted">Curated essentials for skin, hair, baby, oral & elderly wellness</p>
      </div>

      {/* Category Cards */}
      <div className="row justify-content-center mb-5">
        {categories.map((cat) => (
          <div key={cat} className="col-md-2 col-6 mb-3">
            <div className="category-card text-center shadow-sm" onClick={() => handleCategoryClick(cat)}>
              <img
                src={`/images/${cat.toLowerCase().replace(/ /g, '-')}.png`}
                alt={cat}
                className="category-icon mb-2"
              />
              <h6 className="text-primary">{cat}</h6>
            </div>
          </div>
        ))}
      </div>

      {/* Grouped Product Cards */}
      <div className="row">
        {groupedProducts.map(({ category, items }) => (
          items.length > 0 && (
            <div key={category} className="col-md-6 mb-4">
              <div className="category-showcase-card shadow-sm p-3 rounded">
                <h5 className="text-primary mb-3">{category}</h5>
                <div className="d-flex flex-wrap gap-3">
                  {items.map((product, index) => (
                    <div key={index} className="mini-product-card text-center">
                      <img src={product.image} alt={product.name} className="mini-product-image mb-2" />
                      <div className="fw-semibold">{product.name}</div>
                      <div className="text-success">â‚¹{product.price}</div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )
        ))}
      </div>
    </div>
  );
};

export default PersonalCare;
