import axios from "axios";

const baseURL =
  typeof process.env.REACT_APP_API_URL === "string" && process.env.REACT_APP_API_URL.trim()
    ? process.env.REACT_APP_API_URL.trim()
    : "http://localhost:5000/api";

const api = axios.create({
  baseURL,
  withCredentials: true,
});

api.interceptors.request.use(
  config => {
    config.headers["Accept"] = "application/json";

    const token = localStorage.getItem("token");
    console.log("üõ°Ô∏è Token being sent:", token); // ‚úÖ Debug token
    if (token) {
      config.headers["Authorization"] = `Bearer ${token}`;
    }

    const isFormData = config.data instanceof FormData;
    const contentType = config.headers["Content-Type"] || "";
    const isMultipart = typeof contentType === "string" && contentType.includes("multipart/form-data");

    if (!isFormData && !isMultipart) {
      config.headers["Content-Type"] = "application/json";
    }

    return config;
  },
  error => {
    console.error("‚ùå Request error:", error.message);
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  response => response,
  error => {
    const status = error?.response?.status;
    const message = error?.response?.data?.message || error?.message || "Unknown error";

    console.error(`‚ùå API error [${status}]:`, message);

    if (status === 401) {
      console.warn("üîí Session expired. Redirecting to login...");
      window.location.href = "/login";
    }

    return Promise.reject(error);
  }
);

export default api;
