import { useEffect, useState } from "react";
import api from "../api/axios";

export default function Medicines() {
  const [q, setQ] = useState("");
  const [loading, setLoading] = useState(false);
  const [meds, setMeds] = useState([]);
  const [selectedId, setSelectedId] = useState(null);

  async function fetchAll() {
    setLoading(true);
    try {
      const url = q?.trim()
        ? `/api/medicines?search=${encodeURIComponent(q)}`
        : `/api/medicines`;
      const { data } = await api.get(url);
      setMeds(Array.isArray(data) ? data : []);
    } catch (e) {
      console.error("Error fetching medicines:", e);
      setMeds([]);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
  const fetchAll = async () => {
    setLoading(true);
    try {
      const url = q?.trim()
        ? `/api/medicines?search=${encodeURIComponent(q)}`
        : `/api/medicines`;
      const { data } = await api.get(url);
      setMeds(Array.isArray(data) ? data : []);
    } catch (e) {
      console.error("Error fetching medicines:", e);
      setMeds([]);
    } finally {
      setLoading(false);
    }
  };

  fetchAll();
}, []);


  return (
    <div className="container my-5">
      <h2 className="mb-4 text-center text-primary">🩺 Browse Medicines</h2>

      {/* Search Bar */}
      <div className="input-group mb-4">
        <input
          className="form-control"
          placeholder="Search by name, category, or symptoms..."
          value={q}
          onChange={(e) => setQ(e.target.value)}
        />
        <button onClick={fetchAll} className="btn btn-primary">Search</button>
      </div>

      {loading && <p className="text-center">Loading…</p>}
      {!loading && meds.length === 0 && (
        <p className="text-center text-muted">No medicines found.</p>
      )}

      <div className="list-group">
        {meds.map((m) => (
          <div key={m._id} className="list-group-item">
            {/* Collapsed View */}
            <div
              className="d-flex justify-content-between align-items-center"
              style={{ cursor: "pointer" }}
              onClick={() => setSelectedId(selectedId === m._id ? null : m._id)}
            >
              <div>
                <strong>{m.name}</strong>
                <div className="text-muted small">
                  {m.symptoms?.[0] || m.category}
                </div>
              </div>
              <span className="text-primary">
                {selectedId === m._id ? "▲" : "▼"}
              </span>
            </div>

            {/* Expanded View */}
            {selectedId === m._id && (
              <div className="mt-3 border-top pt-3">
                {m.image && (
                  <img
                    src={m.image}
                    alt={m.name}
                    style={{
                      height: "160px",
                      objectFit: "contain",
                      backgroundColor: "#fff",
                      border: "1px solid #eee",
                      borderRadius: "8px",
                      marginBottom: "12px"
                    }}
                  />
                )}
                <p><b>Category:</b> {m.category}</p>
                <p><b>Price:</b> ₹{m.price}</p>
                <p><b>Stock:</b> {m.stock}</p>
                {m.symptoms?.length > 0 && (
                  <p><b>Symptoms:</b> {m.symptoms.join(", ")}</p>
                )}
                {m.dosage && <p><b>Dosage:</b> {m.dosage}</p>}
                {m.description && <p><b>Description:</b> {m.description}</p>}
                <p><b>Prescription:</b> {m.prescriptionRequired}</p>
                <button className="btn btn-success w-100 mt-2">
                  <i className="bi bi-cart-plus me-2"></i>Add to Cart
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
