import { useEffect, useState } from "react";
import api from "../api/axios";
import "./Orders.css";

export default function Orders({ user }) {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await api.get("/orders/my", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrders(res.data.orders || []);
      } catch (err) {
        console.error("‚ùå Failed to fetch orders:", err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) {
    return <div className="container my-5">Loading your orders...</div>;
  }

  return (
    <div className="container my-5">
      <h3 className="mb-4 text-primary">üì¶ Your Orders</h3>
      {orders.length === 0 ? (
        <div className="alert alert-info">You haven‚Äôt placed any orders yet.</div>
      ) : (
        <div className="list-group">
          {orders.map((order, idx) => (
            <div key={order._id || idx} className="list-group-item">
              <h5>Order #{order._id}</h5>
              <p>Status: <strong>{order.status}</strong></p>
              <p>Total: ‚Çπ{order.totalAmount}</p>
              <ul>
                {order.medicines.map((med, i) => (
                  <li key={i}>
                    {med.medicineId?.name || "Unnamed"} √ó {med.quantity} ‚Äî ‚Çπ{med.price}
                  </li>
                ))}
              </ul>
              <small>Placed on: {new Date(order.placedAt).toLocaleString()}</small>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
