// src/components/healthcondition/HealthCondition.jsx
import React, { useState } from 'react';
import './HealthCondition.css';

const filterOptions = [
  "Bone and Joint Care",
  "Digestive Care",
  "Eye Care",
  "Pain Relief",
  "Smoking Cessation",
  "Liver Care",
  "Stomach Care",
  "Cold and Cough",
  "Heart Care",
  "Kidney Care",
  "Respiratory Care",
  "Mental Wellness",
  "Derma Care"
];

const products = [
  { name: 'Calcium Tablets', subcategory: 'Bone and Joint Care', description: 'Supports bone strength and density.', price: 220, image: '/images/health/bone-care.jpg' },
  { name: 'Probiotic Capsules', subcategory: 'Digestive Care', description: 'Improves gut health and digestion.', price: 180, image: '/images/health/digestive.jpg' },
  { name: 'Eye Drops', subcategory: 'Eye Care', description: 'Relieves dryness and irritation.', price: 60, image: '/images/health/eye-care.jpg' },
  // Add more products as needed
];

const HealthCondition = () => {
  const [selectedSubcategory, setSelectedSubcategory] = useState('');

  const filteredProducts = selectedSubcategory
    ? products.filter((p) => p.subcategory === selectedSubcategory)
    : products;

  return (
    <div className="healthcondition-container container py-4">
      {/* Hero Section */}
      <div className="hero-section text-center mb-5">
        <h1 className="display-5 fw-bold text-primary">ðŸ©º Health Condition Products</h1>
        <p className="lead text-muted">Targeted care for every health needâ€”from joints to mental wellness</p>
      </div>

      {/* Category Cards */}
      <div className="row justify-content-center mb-5">
        {filterOptions.map((option) => (
          <div key={option} className="col-md-2 col-6 mb-3">
            <div
              className={`category-card text-center shadow-sm ${
                selectedSubcategory === option ? 'selected' : ''
              }`}
              onClick={() => setSelectedSubcategory(option)}
            >
              <img
                src={`/icons/${option.toLowerCase().replace(/ /g, '-')}.png`}
                alt={option}
                className="category-icon mb-2"
              />
              <h6 className="text-primary">{option}</h6>
            </div>
          </div>
        ))}
        {selectedSubcategory && (
          <div className="text-center mt-3">
            <button className="btn btn-outline-secondary" onClick={() => setSelectedSubcategory('')}>
              Clear Filter
            </button>
          </div>
        )}
      </div>

      {/* Product Grid */}
      <div className="row">
        {filteredProducts.map((product, index) => (
          <div key={index} className="col-md-4 mb-4">
            <div className="card h-100 product-card shadow-sm">
              <img
                src={product.image}
                alt={product.name}
                className="card-img-top"
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <div className="card-body">
                <h6 className="text-muted mb-1">{product.subcategory}</h6>
                <h5 className="card-title text-primary">{product.name}</h5>
                <p className="card-text text-muted">{product.description}</p>
                <p className="fw-bold">â‚¹{product.price}</p>
                <button className="btn btn-success w-100">
                  <i className="bi bi-cart-plus me-2"></i>Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HealthCondition;
