import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api/axios";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./Cart.css";

export default function Cart({ user, token }) {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // ✅ Fallback session sync if props are missing
  useEffect(() => {
    if (!user || !token) {
      const storedUser = localStorage.getItem("user");
      const storedToken = localStorage.getItem("token");

      if (storedUser && storedToken) {
        try {
          const parsedUser = JSON.parse(storedUser);
          if (parsedUser && parsedUser._id) {
            console.log("🧩 Cart fallback session restored:", parsedUser);
            setCartItems([]);
            setLoading(true);
            setTimeout(() => window.dispatchEvent(new Event("storage")), 100);
          }
        } catch (err) {
          console.warn("⚠️ Failed to parse fallback user:", err.message);
        }
      }
    }
  }, [user, token]);

  // ✅ Fetch cart items
  useEffect(() => {
    if (!token || !user?._id) {
      console.warn("🚫 Missing token or user:", user);
      return;
    }

    const fetchCart = async () => {
      try {
        const res = await api.get("/cart", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        console.log("🛒 Cart response:", res.data);
        const data = res.data?.items || [];
        setCartItems(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error("❌ Failed to fetch cart items:", err.message || err);
        toast.error("Could not load cart. Please try again.");
        setCartItems([]);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();

    const addedItem = localStorage.getItem("addedToCart");
    if (addedItem) {
      toast.success(`✅ ${addedItem} added to cart!`);
      localStorage.removeItem("addedToCart");
    }
  }, [token, user]);

  const getTotal = () =>
    cartItems.reduce(
      (sum, item) => sum + (item.price || 0) * (item.quantity || 1),
      0
    );

  const handleCheckout = () => {
    navigate("/checkout");
  };

  const handleBuySingle = (item) => {
    navigate(`/checkout?itemId=${item.medicineId || item._id}`);
  };

  // 🛡️ Session guards
  if (typeof user === "undefined") {
    return (
      <div className="container my-5 text-center">
        <h4 className="text-muted">Initializing user session...</h4>
      </div>
    );
  }

  if (!user || !token) {
    return (
      <div className="container my-5">
        <h3 className="mb-4 text-primary">🛒 Your Cart</h3>
        <div className="alert alert-warning">Session expired. Please log in again.</div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="container my-5">
        <h3 className="mb-4 text-primary">🛒 Your Cart</h3>
        <div className="alert alert-secondary">Loading cart items...</div>
      </div>
    );
  }

  return (
    <div className="container my-5">
      <ToastContainer position="top-right" autoClose={3000} />
      <h3 className="mb-4 text-primary">🛒 Your Cart</h3>

      {cartItems.length === 0 ? (
        <div className="alert alert-info">Your cart is empty.</div>
      ) : (
        <>
          <div className="list-group mb-4">
            {cartItems.map((item, idx) => (
              <div
                key={item.medicineId || `${item.name}-${idx}`}
                className="list-group-item d-flex justify-content-between align-items-center cart-item"
              >
                <div>
                  <h5 className="mb-1">{item.name || "Unnamed Medicine"}</h5>
                  <small className="text-muted">
                    ₹{item.price || 0} × {item.quantity || 1}
                  </small>
                  {item.description && (
                    <p className="mt-1 text-muted small">{item.description}</p>
                  )}
                </div>

                <div className="d-flex flex-column align-items-end">
                  <strong>₹{(item.price || 0) * (item.quantity || 1)}</strong>
                  <button
                    className="btn btn-sm btn-outline-success mt-2"
                    onClick={() => handleBuySingle(item)}
                  >
                    Buy Now
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="d-flex justify-content-between align-items-center mb-4 cart-total">
            <h5>Total:</h5>
            <h5 className="text-success">₹{getTotal()}</h5>
          </div>

          <button
            className="btn btn-success w-100 cart-checkout-btn"
            onClick={handleCheckout}
          >
            🧾 Proceed to Buy All
          </button>
        </>
      )}
    </div>
  );
}
