import { useLocation } from "react-router-dom";
import { useState } from "react";

export default function BuyMedicine() {
  const { state } = useLocation();
  const medicine = state?.medicine;
  const [prescriptionFile, setPrescriptionFile] = useState(null);

  if (!medicine) return <p className="text-center">No medicine selected.</p>;

  const handleFileChange = (e) => {
    const file = e.target.files?.[0];
    setPrescriptionFile(file || null);
  };

  const isPrescriptionRequired = medicine.prescriptionRequired === "Required";
  const canProceed = !isPrescriptionRequired || prescriptionFile;

  // âœ… Use public image fallback if medicine.image is missing
  const imageUrl = medicine.image
    ? medicine.image.startsWith("/uploads")
      ? `http://localhost:5000${medicine.image}`
      : medicine.image
    : "/images/azivo.jpg"; // fallback from public/images

  return (
    <div className="container my-5">
      <h2 className="mb-4 text-center text-success">ðŸ›’ Buy Medicine</h2>

      <div className="card p-4 shadow-sm">
        {/* âœ… Medicine Name First */}
        <h4 className="text-center mb-3">{medicine.name}</h4>

        {/* âœ… Image Directly Below Name */}
        <div className="text-center mb-4">
          <img
            src={imageUrl}
            alt={medicine.name}
            style={{
              height: "180px",
              objectFit: "contain",
              borderRadius: "8px",
              border: "1px solid #211a1aff",
              backgroundColor: "#fff",
              maxWidth: "100%",
            }}
          />
        </div>

        {/* âœ… Medicine Details */}
        <p><b>Category:</b> {medicine.category}</p>
        <p><b>Price:</b> â‚¹{medicine.price}</p>
        <p><b>Stock:</b> {medicine.stock}</p>
        <p><b>Dosage:</b> {medicine.dosage}</p>
        <p><b>Symptoms:</b> {medicine.symptoms?.join(", ")}</p>
        <p><b>Description:</b> {medicine.description}</p>
        <p><b>Prescription:</b> {medicine.prescriptionRequired}</p>

        {/* âœ… Prescription Upload */}
        {isPrescriptionRequired && (
          <div className="mt-4">
            <label className="form-label"><b>Upload Prescription</b></label>
            <input
              type="file"
              className="form-control"
              accept="image/*,.pdf"
              onChange={handleFileChange}
            />
            {prescriptionFile && (
              <p className="mt-2 text-success">
                âœ… File selected: <i>{prescriptionFile.name}</i>
              </p>
            )}
          </div>
        )}

        {/* âœ… Action Button */}
        <button
          className="btn btn-primary mt-4 w-100"
          disabled={!canProceed}
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
}
