import React, { useState, useEffect } from "react";

const MedicineSearch = () => {
  const [query, setQuery] = useState("");
  const [category, setCategory] = useState("");
  const [symptom, setSymptom] = useState("");
  const [medicines, setMedicines] = useState([]);

  // Function to fetch medicines
  const fetchMedicines = async () => {
    try {
      const response = await fetch(
        `/api/medicines?name=${query}&category=${category}&symptom=${symptom}`
      );
      const data = await response.json();
      setMedicines(data);
    } catch (error) {
      console.error("Error fetching medicines:", error);
    }
  };

  useEffect(() => {
    fetchMedicines();
  }, [query, category, symptom]);

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Search Medicines</h2>

      {/* Search by Name */}
      <input
        type="text"
        placeholder="Search by name..."
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        className="w-full p-2 mb-3 border rounded"
      />

      {/* Search by Category */}
      <select
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        className="w-full p-2 mb-3 border rounded"
      >
        <option value="">Select Category</option>
        <option value="antibiotic">Antibiotic</option>
        <option value="painkiller">Painkiller</option>
        <option value="antipyretic">Antipyretic</option>
        <option value="vitamin">Vitamin</option>
      </select>

      {/* Search by Symptom */}
      <select
        value={symptom}
        onChange={(e) => setSymptom(e.target.value)}
        className="w-full p-2 mb-3 border rounded"
      >
        <option value="">Select Symptom</option>
        <option value="fever">Fever</option>
        <option value="headache">Headache</option>
        <option value="cold">Cold</option>
        <option value="pain">Pain</option>
      </select>

      {/* Medicines List */}
      <div className="mt-4">
        {medicines.length === 0 ? (
          <p className="text-gray-600">No medicines found.</p>
        ) : (
          <ul className="space-y-2">
            {medicines.map((med) => (
              <li
                key={med._id}
                className="p-3 border rounded shadow-sm bg-white"
              >
                <h3 className="font-semibold">{med.name}</h3>
                <p className="text-sm text-gray-600">
                  Category: {med.category} | Symptom: {med.symptom}
                </p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default MedicineSearch;
