  { name: "Digene Mint Flavour Gel 450ml", description: "Save â‚¹42 | MRP â‚¹210.00", price: 168, image: "/images/Digene.jpg", discount: "20% OFF" },
  { name: "Softovac Sf Powder 250gm", description: "Save â‚¹72 | MRP â‚¹332.00", price: 260, image: "/images/softovac-250.jpg", discount: "22% OFF" },
  { name: "Gaviscon Peppermint Liquid 150ml)", description: "Save â‚¹24 | MRP â‚¹132.00", price: 108, image: "/images/gasofast.jpg", discount: "18% OFF" },
  { name: "Baidyanath Triphala Churna 500gm", description: "Save â‚¹65 | MRP â‚¹150.00", price: 85, image: "/images/triphala.jpg", discount: "43% OFF" },
  { name: "Omee Mint Flavour Chewable Tablet 12", description: "Save â‚¹15 | MRP â‚¹100.00", price: 85, image: "/images/enzyme.jpg", discount: "15% OFF" },
  { name: "Etazyme Pineapple Flavoured Syrup 200ml", description: "Save â‚¹21.25 | MRP â‚¹106.25", price: 85, image: "/images/cremalax-170.jpg", discount: "20% OFF" },
  { name: "Cremaffin Mixed Fruit Flavour Emulsion 225ml", description: "Save â‚¹198 | MRP â‚¹450.00", price: 252, image: "/images/healthduphalac.jpg", discount: "44% OFF" },
  { name: "Duphalac Lemon Flavour Solution 450ml", description: "Save â‚¹300 | MRP â‚¹750.00", price: 450, image: "/images/health/tummystar.jpg", discount: "40% OFF" },
  { name: "Tummy Saaf Churan 80gm+20gm", description: "Save â‚¹30 | MRP â‚¹150.00", price: 120, image: "/images/health/petsaffa.jpg", discount: "20% OFF" },
  { name: "Pet Saffa Tablet 30", description: "Save â‚¹130 | MRP â‚¹590.00", price: 460, image: "/images/health/softovac-500.jpg", discount: "22% OFF" },
  { name: "Softovac Sf Powder 450gm", description: "Save â‚¹20 | MRP â‚¹105.00", price: 85, image: "/images/health/unienzyme.jpg", discount: "19% OFF" },
  { name: "Unienzyme Tablet 15", description: "Save â‚¹45 | MRP â‚¹225.00", price: 180, image: "/images/health/cremalax-450.jpg", discount: "20% OFF" },
];

const DigestiveCare = () => {
  return (
    <div className="digestivecare-container container py-4">
      <h2 className="text-center mb-4">ðŸ§ƒ Digestive Care</h2>

      <div className="row">
        {products.map((product, index) => (
          <div key={index} className="col-md-4 mb-4">
            <div className="card h-100 product-card shadow-sm position-relative">
              <div className="position-absolute top-0 end-0 bg-danger text-white px-2 py-1 rounded-start">
                {product.discount}
              </div>
              <img
                src={product.image}
                alt={product.name}
                className="card-img-top"
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <div className="card-body">
                <h5 className="card-title text-primary">{product.name}</h5>
                <p className="card-text text-muted">{product.description}</p>
                <p className="fw-bold">â‚¹{product.price}</p>
                <button className="btn btn-success w-100">
                  <i className="bi bi-cart-plus me-2"></i>Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DigestiveCare;
