import React from "react";
import { useNavigate } from "react-router-dom";

export default function UserPanel({ user, onClose }) {
  const navigate = useNavigate();

  return (
    <div
      className="card shadow-sm p-4"
      style={{
        position: "absolute",
        top: "70px",
        right: "20px",
        width: "320px",
        zIndex: 1000,
        borderRadius: "12px",
        backgroundColor: "#fff",
      }}
    >
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h5 className="text-primary mb-0">ðŸ‘¤ Account</h5>
        <button className="btn btn-sm btn-outline-secondary" onClick={onClose}>âœ–</button>
      </div>

      <p><strong>Name:</strong> {user.name}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Role:</strong> {user.role}</p>

      <hr />

      <div className="d-grid gap-2 mb-3">
        <button className="btn btn-outline-primary" onClick={() => navigate("/profile")}>
          ðŸ‘¤ My Profile
        </button>
        <button className="btn btn-outline-secondary" onClick={() => navigate("/orders")}>
          ðŸ“¦ Orders
        </button>
        <button className="btn btn-outline-success" onClick={() => navigate("/profile?tab=prescriptions")}>
          ðŸ§¾ Prescription Status
        </button>
      </div>

      <hr />

      <button className="btn btn-outline-danger w-100" onClick={() => {
        localStorage.removeItem("user");
        navigate("/login");
      }}>
        ðŸ”“ Logout
      </button>

      <div className="mt-4 text-center text-muted" style={{ fontSize: "14px" }}>
        ðŸ“± Download our mobile app<br />
        <a href="/app" className="text-decoration-none">Take MediQ with you</a>
      </div>
    </div>
  );
}
