import { Link } from "react-router-dom";
import { useEffect } from "react";

function Navbar() {
  useEffect(() => {
    const searchBar = document.getElementById("search-bar");
    const navbarSlot = document.getElementById("navbar-search-slot");
    const heroSection = document.querySelector(".hero");

    const handleScroll = () => {
      if (window.scrollY > heroSection.offsetHeight - 120) {
        if (navbarSlot && !navbarSlot.contains(searchBar)) {
          navbarSlot.appendChild(searchBar);
          searchBar.classList.add("compact-search");
        }
      } else {
        if (heroSection && !heroSection.contains(searchBar)) {
          heroSection.appendChild(searchBar);
          searchBar.classList.remove("compact-search");
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light shadow-sm">
      <div className="container-fluid">
        {/* Logo */}
        <Link className="navbar-brand d-flex align-items-center" to="/">
          <img
            src="/images/logo.png"
            alt="Logo"
            width="45"
            height="45"
            className="me-2"
          />
          <span className="fw-bold text-primary">
            Medi<span className="text-dark">Q</span>
          </span>
        </Link>

        {/* Toggler (mobile) */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Links */}
        <div className="collapse navbar-collapse" id="navbarNav">
          {/* Left links */}
          <ul className="navbar-nav mx-auto">
            <li className="nav-item">
              <Link className="nav-link" to="/">
                üè† Home
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/medicines">
                üíä Shop Medicines
              </Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/upload">
                üì§ Upload Prescription
              </Link>
            </li>
          </ul>

          {/* Right actions */}
          <ul className="navbar-nav ms-auto align-items-center">
            {/* Search bar slot */}
            <div id="navbar-search-slot" className="d-none d-lg-block me-3"></div>

            <li className="nav-item">
              <Link className="nav-link" to="/cart">
                üõí My Cart
              </Link>
            </li>
            <li className="nav-item">
              <Link
                className="btn btn-primary ms-lg-3 mt-2 mt-lg-0"
                to="/login"
              >
                üîë Login / Register
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
