import React, { useEffect, useState } from "react";
import api from "../api/axios";
import "./Ayurveda.css";

export default function Ayurveda() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAyurveda = async () => {
      try {
        const res = await api.get("/medicines/category", {
          params: { type: "ayurveda" },
        });
        setProducts(res.data || []);
      } catch (err) {
        console.error("❌ Ayurveda fetch error:", err.message);
        setProducts([]);
      } finally {
        setLoading(false);
      }
    };

    fetchAyurveda();
  }, []);

  const renderSection = (title, items) => (
    <section className="ayurveda-section mb-5">
      <h3 className="section-title">{title}</h3>
      <div className="row">
        {items.map((item) => (
          <div key={item._id} className="col-md-4 mb-4">
            <div className="card ayurveda-card h-100 shadow-sm">
              <img
                src={item.image || "/assets/default-ayurveda.png"}
                alt={item.name}
                className="card-img-top"
              />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{item.name}</h5>
                <p className="card-text">{item.description || "Herbal remedy for wellness."}</p>
                <div className="price-box mt-auto">
                  <span className="text-muted text-decoration-line-through">₹{item.mrp}</span>
                  <span className="fw-bold ms-2 text-success">₹{item.price}</span>
                  <span className="badge bg-success ms-2">{item.discount || "15% OFF"}</span>
                </div>
                <button className="btn btn-outline-success mt-3">Add</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );

  return (
    <div className="container my-5">
      <div className="ayurveda-header text-center mb-5">
        <h2 className="text-success">🌿 MediQ Ayurvedic Store</h2>
        <p className="text-muted">Rooted in Kerala’s healing traditions</p>
      </div>

      {loading ? (
        <div className="text-center text-muted">Loading Ayurvedic products...</div>
      ) : products.length === 0 ? (
        <div className="alert alert-info text-center">No Ayurvedic medicines available.</div>
      ) : (
        <>
          {renderSection("Top Ayurvedic Treasures", products.slice(0, 6))}
          {renderSection("Specialty Ayurvedic Picks", products.slice(6, 12))}
          {renderSection(
            "Juices for Wellness",
            products.filter((item) => item.name.toLowerCase().includes("juice"))
          )}
        </>
      )}
    </div>
  );
}
