import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from "react-router-dom";
import Navbar from "./components/Navbar";
import CategoryNavbar from "./components/CategoryNavbar";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import UploadPrescription from "./pages/UploadPrescription";
import Medicines from "./pages/Medicines";
import BuyMedicine from "./pages/BuyMedicine";
import UserProfile from "./pages/UserProfile";
import Checkout from "./pages/Checkout";
import Unauthorized from "./pages/Unauthorized";
import PrescriptionStatus from "./pages/PrescriptionStatus";
import Cart from "./pages/Cart";
import PharmacistDashboard from "./pages/PharmacistDashboard";
import MedicineManager from "./pages/MedicineManager";
import UploadMedicine from "./pages/UploadMedicine";
import PersonalCare from "./pages/PersonalCare";
import SkinCare from "./pages/SkinCare";
import HairCare from "./pages/HairCare";
import BabyMomCare from "./pages/BabyMomCare";
import OralCare from "./pages/OralCare";
import ElderlyCare from "./pages/ElderlyCare";
import HealthCondition from "./pages/HealthCondition";
import BoneJointCare from "./pages/BoneJointCare";
import DigestiveCare from "./pages/DigestiveCare";
import EyeCare from "./pages/EyeCare";
import PainRelief from "./pages/PainRelief";
import SmokingCessation from "./pages/SmokingCessation";
import LiverCare from "./pages/LiverCare";
import ColdCough from "./pages/ColdCough";
import HeartCare from "./pages/HeartCare";
import KidneyCare from "./pages/KidneyCare";
import RespiratoryCare from "./pages/RespiratoryCare";
import MentalWellness from "./pages/MentalWellness";
import DermaCare from "./pages/DermaCare";
import VitaminsPage from "./pages/VitaminsPage";

export default function App() {
  const [scrolled, setScrolled] = useState(false);
  const [user, setUser] = useState(undefined);
  const [token, setToken] = useState(null);
  const [query, setQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [selectedMedicineId, setSelectedMedicineId] = useState(null);
  const location = useLocation();

  // ✅ Restore session from localStorage on route change
  useEffect(() => {
    const syncSession = () => {
      try {
        const storedUser = localStorage.getItem("user");
        const storedToken = localStorage.getItem("token");

        console.log("📦 Raw user:", storedUser);
        console.log("📦 Raw token:", storedToken);

        if (storedUser && storedToken) {
          const parsedUser = JSON.parse(storedUser);
          if (parsedUser && parsedUser._id) {
            setUser(parsedUser);
            setToken(storedToken);
            console.log("✅ Session restored:", parsedUser);
            return;
          }
        }

        setUser(null);
        setToken(null);
        console.warn("⚠️ Session not found or invalid");
      } catch (err) {
        console.error("❌ Session sync error:", err.message);
        setUser(null);
        setToken(null);
      }
    };

    syncSession();
  }, [location]);

  // ✅ Debug session changes
  useEffect(() => {
    console.log("👤 User state updated:", user);
    console.log("🔐 Token state updated:", token);
  }, [user, token]);

  // ✅ Scroll detection
  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 100);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // ✅ Navbar height sync
  useEffect(() => {
    const syncNavbarHeight = () => {
      const navbar = document.querySelector(".navbar");
      if (navbar) {
        const height = navbar.offsetHeight;
        document.documentElement.style.setProperty("--main-navbar-height", `${height}px`);
      }
    };
    syncNavbarHeight();
    window.addEventListener("resize", syncNavbarHeight);
    return () => window.removeEventListener("resize", syncNavbarHeight);
  }, []);

  // ✅ Medicine search suggestions
  useEffect(() => {
    const fetchSuggestions = async () => {
      if (query.trim().length < 2) {
        setSuggestions([]);
        return;
      }

      try {
        const res = await api.get("/medicines/search", { params: { q: query } });
        setSuggestions(res.data || []);
      } catch (err) {
        console.error("❌ Search error:", err.message);
      }
    };

    const debounce = setTimeout(fetchSuggestions, 300);
    return () => clearTimeout(debounce);
  }, [query]);

  // ✅ Show loading screen while session is undefined
  if (user === undefined) {
    return (
      <div className="container my-5 text-center">
        <h4 className="text-muted">Initializing user session...</h4>
      </div>
    );
  }

 return (
  <>
    <Navbar scrolled={scrolled} query={query} setQuery={setQuery} />
    <CategoryNavbar scrolled={scrolled} />

    <Routes>
      {/* Public Routes */}
      <Route path="/" element={<Home scrolled={scrolled} user={user} />} />
      <Route path="/login" element={<Login setUser={setUser} setToken={setToken} />} />
      <Route path="/register" element={<Register />} />
      <Route path="/upload" element={<UploadPrescription user={user} token={token} />} />
      <Route path="/medicines" element={<Medicines user={user} token={token} />} />
      <Route path="/buy/:id" element={<BuyMedicine user={user} token={token} />} />
      <Route path="/profile" element={<UserProfile user={user} />} />
      <Route path="/checkout" element={<Checkout user={user} token={token} />} />
      <Route path="/unauthorized" element={<Unauthorized />} />
      <Route path="/prescriptions" element={<PrescriptionStatus user={user} token={token} />} />
      <Route path="/cart" element={<Cart user={user} token={token} />} />

      {/* Pharmacist Routes */}
      {[
        { path: "/pharmacist", component: PharmacistDashboard },
        { path: "/manage-medicines", component: MedicineManager },
        { path: "/upload-medicine", component: UploadMedicine }
      ].map(({ path, component: Component }) => (
        <Route
          key={path}
          path={path}
          element={
            user?.role === "pharmacist" ? (
              <Component user={user} token={token} />
            ) : user ? (
              <Navigate to="/unauthorized" />
            ) : (
              <div className="container text-center mt-5">
                <h4 className="text-danger">🔒 Please log in to access this page</h4>
              </div>
            )
          }
        />
      ))}

      {/* Personal Care */}
      <Route path="/personal-care" element={<PersonalCare />} />
      <Route path="/personal-care/skin" element={<SkinCare />} />
      <Route path="/personal-care/hair" element={<HairCare />} />
      <Route path="/personal-care/baby-mom" element={<BabyMomCare />} />
      <Route path="/personal-care/oral" element={<OralCare />} />
      <Route path="/personal-care/elderly" element={<ElderlyCare />} />

      {/* Health Conditions */}
      <Route path="/health-conditions" element={<HealthCondition />} />
      <Route path="/health-conditions/bone-joint" element={<BoneJointCare />} />
      <Route path="/health-conditions/digestive" element={<DigestiveCare />} />
      <Route path="/health-conditions/eye" element={<EyeCare />} />
      <Route path="/health-conditions/pain" element={<PainRelief />} />
      <Route path="/health-conditions/smoking" element={<SmokingCessation />} />
      <Route path="/health-conditions/liver" element={<LiverCare />} />
      <Route path="/health-conditions/cold-cough" element={<ColdCough />} />
      <Route path="/health-conditions/heart" element={<HeartCare />} />
      <Route path="/health-conditions/kidney" element={<KidneyCare />} />
      <Route path="/health-conditions/respiratory" element={<RespiratoryCare />} />
      <Route path="/health-conditions/mental" element={<MentalWellness />} />
      <Route path="/health-conditions/derma" element={<DermaCare />} />

      {/* Vitamins */}
      <Route path="/vitamins" element={<VitaminsPage />} />

      {/* Fallback */}
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>

    {/* ✅ Redirect to selected medicine from search */}
    {selectedMedicineId && (
      <Navigate to={`/buy/${selectedMedicineId}`} replace />
    )}

    {/* ✅ Search Suggestions */}
    {suggestions.length > 0 && (
      <div className="container search-suggestions-container mt-4">
        <h5 className="mb-3 text-primary">
          Showing results for: <span className="text-dark">{query}</span>
        </h5>
        <div className="row">
          {suggestions.map((med) => (
            <div key={med._id} className="col-md-4 mb-4">
              <div className="medicine-card h-100 d-flex flex-column justify-content-between p-3 border rounded shadow-sm">
                <div>
                  <h6 className="fw-bold">{med.name}</h6>
                  <p><strong>Category:</strong> {med.category}</p>
                  <p><strong>Symptoms:</strong> {med.symptoms?.join(", ")}</p>
                  <p className="text-muted small">{med.description}</p>
                  <p><strong>Price:</strong> ₹{med.price}</p>
                </div>
                <button
                  className="btn btn-outline-primary mt-3"
                  onClick={() => {
                    setSelectedMedicineId(med._id);
                    window.scrollTo({ top: 0, behavior: "smooth" });
                  }}
                >
                  View
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    )}
  </>
);
}
