import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [medicines, setMedicines] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/medicines") // adjust if your backend runs on another port
      .then((response) => {
        setMedicines(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching medicines:", error);
        setLoading(false);
      });
  }, []);

  if (loading) return <h2>Loading medicines...</h2>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>ðŸ’Š Available Medicines</h1>
      {medicines.length === 0 ? (
        <p>No medicines found.</p>
      ) : (
        <ul>
          {medicines.map((med) => (
            <li key={med._id}>
              <strong>{med.name}</strong> - {med.price}â‚¹ <br />
              <em>{med.description}</em>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
