// src/components/personalcare/PersonalCare.jsx
import React, { useState } from 'react';
import './PersonalCare.css'; // optional styling

const categories = [
  'Skin Care',
  'Hair Care',
  'Baby and Mom Care',
  'Oral Care',
  'Elderly Care',
];

const products = [
  { name: 'Aloe Vera Gel', category: 'Skin Care' },
  { name: 'Anti-Dandruff Shampoo', category: 'Hair Care' },
  { name: 'Baby Lotion', category: 'Baby and Mom Care' },
  
  { name: 'Toothpaste', category: 'Oral Care' },
  { name: 'Joint Pain Cream', category: 'Elderly Care' },
];

const PersonalCare = () => {
  const [selectedCategory, setSelectedCategory] = useState('Skin Care');

  const filteredProducts = products.filter(
    (product) => product.category === selectedCategory
  );

  return (
    <div className="personal-care-container">
      <h2>Personal Care</h2>

      <div className="category-selector">
        {categories.map((cat) => (
          <label key={cat} className="category-option">
            <input
              type="radio"
              name="personalCareCategory"
              value={cat}
              checked={selectedCategory === cat}
              onChange={() => setSelectedCategory(cat)}
            />
            {cat}
          </label>
        ))}
      </div>

      <div className="product-list">
        <h3>{selectedCategory} Products</h3>
        <ul>
          {filteredProducts.map((item, index) => (
            <li key={index}>{item.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default PersonalCare;
