import React from "react";

function CategorySection() {
  const categories = [
    { name: "Medicines", image: "/images/categories/medicines.jpg" },
    { name: "Personal Care", image: "/images/categories/personalcare.jpg" },
    { name: "Health Care", image: "/images/categories/healthcare.jpg" },
    { name: "Vitamins", image: "/images/categories/vitamins.jpg" },
    { name: "Ayurveda", image: "/images/categories/ayurveda.jpg" },
    { name: "Derma Care", image: "/images/categories/dermacare.jpg" },
    { name: "Women", image: "/images/categories/women.jpg" },
    { name: "Mom & Baby", image: "/images/categories/momandbaby.jpg" },
    { name: "Health Blogs", image: "/images/categories/healthblogs.jpg" },
  ];

  return (
    <section className="category-section container my-5">
      <h2 className="text-center mb-4">Shop by Category</h2>
      <div className="row g-4 text-center">
        {categories.map((cat, index) => (
          <div key={index} className="col-6 col-md-4 col-lg-3">
            <div className="category-card shadow-sm rounded">
              <img
                src={cat.image}
                alt={cat.name}
                className="category-img"
              />
              <h5 className="mt-2">{cat.name}</h5>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default CategorySection;
