import React, { useEffect, useState } from "react";
import axios from "axios";
import { Navigate } from "react-router-dom";

function PharmacistDashboard({ user }) {
  const [prescriptions, setPrescriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [unauthorized, setUnauthorized] = useState(false);

  useEffect(() => {
    const fetchPrescriptions = async () => {
      if (!user || user.role !== "pharmacist") {
        setUnauthorized(true);
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get("/api/prescriptions");
        setPrescriptions(res.data);
      } catch (err) {
        console.error("Error fetching prescriptions:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchPrescriptions();
  }, [user]);

  const handleAction = async (id, action) => {
    try {
      await axios.patch(`/api/prescriptions/${id}/${action}`);
      setPrescriptions(prev =>
        prev.map(p =>
          p._id === id ? { ...p, status: action === "approve" ? "Approved" : "Rejected" } : p
        )
      );
    } catch (err) {
      console.error(`Error during ${action}:`, err);
    }
  };

  if (unauthorized) {
    return <Navigate to="/unauthorized" />;
  }

  return (
    <div className="container my-5">
      <h2 className="mb-4">üßë‚Äç‚öïÔ∏è Pharmacist Dashboard</h2>

      {loading ? (
        <p>Loading prescriptions...</p>
      ) : prescriptions.length === 0 ? (
        <p>No prescriptions found.</p>
      ) : (
        <table className="table table-bordered table-hover">
          <thead className="table-light">
            <tr>
              <th>User ID</th>
              <th>File Name</th>
              <th>Original Name</th>
              <th>Uploaded At</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {prescriptions.map(p => (
              <tr key={p._id}>
                <td>{p.userId}</td>
                <td>{p.fileName || p.file}</td>
                <td>{p.originalName || "‚Äî"}</td>
                <td>{new Date(p.createdAt).toLocaleString()}</td>
                <td>
                  <span className={`badge bg-${p.status === "Approved" ? "success" : p.status === "Rejected" ? "danger" : "warning"} text-dark`}>
                    {p.status}
                  </span>
                </td>
                <td>
                  <button
                    className="btn btn-success btn-sm me-2"
                    disabled={p.status === "Approved"}
                    onClick={() => handleAction(p._id, "approve")}
                  >
                    ‚úÖ Approve
                  </button>
                  <button
                    className="btn btn-danger btn-sm"
                    disabled={p.status === "Rejected"}
                    onClick={() => handleAction(p._id, "reject")}
                  >
                    ‚ùå Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default PharmacistDashboard;
