import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import CategoryNavbar from "./components/CategoryNavbar";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import UploadPrescription from "./pages/UploadPrescription";
import PharmacistDashboard from "./pages/PharmacistDashboard";
import "./App.css";
import "./index.css";

export default function App() {
  const [scrolled, setScrolled] = useState(false);

  // ðŸ” Simulated user object (replace with real auth logic)
  const user = JSON.parse(localStorage.getItem("user")); // or useContext(AuthContext)

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 100);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    const navbar = document.querySelector(".navbar");
    if (navbar) {
      const height = navbar.offsetHeight;
      document.documentElement.style.setProperty("--main-navbar-height", `${height}px`);
    }
  }, []);

  return (
    <Router>
      <Navbar scrolled={scrolled} />
      <CategoryNavbar scrolled={scrolled} />

      <Routes>
        <Route path="/" element={<Home scrolled={scrolled} />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/upload" element={<UploadPrescription />} />

        {/* âœ… Protected Pharmacist Route */}
        <Route
          path="/pharmacist"
          element={
            user?.role === "pharmacist" ? (
              <PharmacistDashboard user={user} />
            ) : (
              <Navigate to="/unauthorized" />
            )
          }
        />

        {/* ðŸ”§ Future routes */}
        {/* <Route path="/cart" element={<Cart />} /> */}
        {/* <Route path="/orders" element={<Orders />} /> */}
      </Routes>
    </Router>
  );
}
