import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

// ----------------------------------------------------
// Mock Data (For UI demonstration)
// ----------------------------------------------------
const MOCK_AYURVEDIC_ITEMS = [
  { _id: 'a1', name: 'Triphala Churna', price: 299, description: 'Digestive & Detox Support', image: 'https://placehold.co/120x120/E8F5E9/4CAF50?text=Triphala' },
  { _id: 'a2', name: 'Ashwagandha Capsules', price: 450, description: 'Stress Relief & Energy Boost', image: 'https://placehold.co/120x120/FFF3E0/FF9800?text=Ashwa' },
  { _id: 'a3', name: 'Brahmi Oil', price: 199, description: 'Promotes Hair & Scalp Health', image: 'https://placehold.co/120x120/E0F7FA/00BCD4?text=Brahmi' },
  { _id: 'a4', name: 'Chyawanprash', price: 599, description: 'Immunity & Vitality Booster', image: 'https://placehold.co/120x120/F3E5F5/9C27B0?text=Chyawan' },
  { _id: 'a5', name: 'Neem Face Wash', price: 150, description: 'Natural Acne & Pimple Care', image: 'https://placehold.co/120x120/FBE9E7/FF5722?text=Neem' },
  { _id: 'a6', name: 'Kumkumadi Oil', price: 899, description: 'Skin Glow & Complexion', image: 'https://placehold.co/120x120/E1F5FE/2196F3?text=Kumku' },
];

// Placeholder for the external API utility
const api = {
    get: async (url, config) => {
        // Mocking the API response for Ayurveda category
        return new Promise(resolve => {
            setTimeout(() => {
                resolve({ data: MOCK_AYURVEDIC_ITEMS });
            }, 500); // Simulate network latency
        });
    }
};

// ----------------------------------------------------
// Product Card Component
// ----------------------------------------------------
const AyurvedicItemCard = ({ item }) => (
    <div 
        className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-1 overflow-hidden border border-green-100"
    >
        <div className="p-4 flex flex-col items-center">
            {/* Image Placeholder */}
            <div className="h-32 w-32 mb-4 rounded-full bg-gray-100 flex items-center justify-center border-4 border-green-500/50">
                <img 
                    src={item.image} 
                    alt={item.name} 
                    className="h-28 w-28 object-cover rounded-full"
                    onError={(e) => e.target.src = 'https://placehold.co/120x120/E8F5E9/4CAF50?text=Ayur'} // Fallback
                />
            </div>
            
            <h3 className="text-xl font-serif font-bold text-gray-800 text-center mb-1 leading-snug">
                {item.name}
            </h3>
            <p className="text-sm text-amber-700 font-semibold mb-3 text-center">
                {item.description}
            </p>
            
            <div className="flex justify-between items-center w-full mt-auto pt-2 border-t border-gray-100">
                <span className="text-2xl font-extrabold text-green-700">
                    ‚Çπ{item.price}
                </span>
                <Link 
                    to={`/buy/${item._id}`} 
                    className="px-4 py-2 bg-green-600 text-white font-semibold rounded-full shadow-md hover:bg-green-700 transition duration-200 text-sm"
                >
                    Buy Now
                </Link>
            </div>
        </div>
    </div>
);


// ----------------------------------------------------
// Main Ayurveda Component
// ----------------------------------------------------
export default function Ayurveda({ user }) {
  const [ayurvedicItems, setAyurvedicItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAyurveda = async () => {
      try {
        // --- REAL API CALL (Commented out for single-file environment) ---
        // const res = await api.get("/medicines/category", {
        //   params: { type: "ayurveda" },
        // });
        // setAyurvedicItems(res.data || []);
        
        // --- MOCK API CALL (For UI demonstration) ---
        const res = await api.get("/mock/ayurveda");
        setAyurvedicItems(res.data || []);

      } catch (err) {
        console.error("‚ùå Ayurveda fetch error:", err.message);
        setAyurvedicItems([]);
      } finally {
        setLoading(false);
      }
    };

    fetchAyurveda();
  }, []);

  return (
    <div className="min-h-screen bg-green-50/70 pb-16 pt-8 font-serif">
      {/* 1. Hero Section: Nature-Inspired Banner */}
      <header className="bg-gradient-to-r from-green-500 to-green-700 text-white py-16 mb-10 shadow-xl">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <span className="text-5xl md:text-6xl mb-4 inline-block transform rotate-6">üåø</span>
          <h1 className="text-5xl md:text-7xl font-extrabold tracking-tight mb-2 font-serif">
            Ayurveda Essentials
          </h1>
          <p className="mt-3 text-lg md:text-xl font-light max-w-3xl mx-auto">
            The ancient wisdom of India, delivered to your doorstep. Pure, natural, and effective remedies for holistic well-being.
          </p>
        </div>
      </header>

      {/* 2. Main Content and Product Grid */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <h2 className="text-3xl font-bold text-green-800 mb-8 border-b-2 border-amber-500/50 pb-2 flex items-center">
          <span className="mr-2 text-amber-700">üåº</span> Our Authentic Products
        </h2>

        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-b-4 border-green-500"></div>
            <p className="text-lg text-gray-600 ml-4">Connecting with Nature's Pharmacy...</p>
          </div>
        ) : ayurvedicItems.length === 0 ? (
          <div className="p-8 bg-amber-100 text-amber-800 rounded-xl border border-amber-300 shadow-sm text-center">
            <p className="font-semibold">üôè No Ayurvedic medicines currently available.</p>
            <p className="text-sm mt-1">Please check back later as we replenish our stock of natural remedies.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
            {ayurvedicItems.map((item) => (
              <AyurvedicItemCard key={item._id} item={item} />
            ))}
          </div>
        )}
      </div>

      {/* 3. Principles Section (Informational CTA) */}
      <section className="mt-16 bg-white shadow-inner py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
            <h2 className="text-3xl font-bold text-green-800 mb-4 font-serif">
                Understanding the Balance
            </h2>
            <p className="text-gray-600 max-w-4xl mx-auto mb-6">
                Ayurveda is based on the principle of harmony among the five elements and the three Doshas (Vata, Pitta, Kapha). Explore how these ancient practices can benefit your modern life.
            </p>
            <Link 
                to="/articles/ayurvedic-principles"
                className="inline-flex items-center px-6 py-3 border border-transparent text-base font-medium rounded-full shadow-lg text-white bg-amber-600 hover:bg-amber-700 transition duration-300"
            >
                Read Ayurvedic Principles Guide
                <span className="ml-2 text-xl">üßò‚Äç‚ôÄÔ∏è</span>
            </Link>
        </div>
      </section>

    </div>
  );
}
