import React, { useEffect, useState } from "react";
import api from "../api/axios";
import { Link } from "react-router-dom";
import "./Ayurveda.css"; // ‚úÖ Add this for styling

export default function Ayurveda({ user }) {
  const [ayurvedicItems, setAyurvedicItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAyurveda = async () => {
      try {
        const res = await api.get("/medicines/category", {
          params: { type: "ayurveda" },
        });
        setAyurvedicItems(res.data || []);
      } catch (err) {
        console.error("‚ùå Ayurveda fetch error:", err.message);
        setAyurvedicItems([]);
      } finally {
        setLoading(false);
      }
    };

    fetchAyurveda();
  }, []);

  return (
    <div className="container my-5">
      <div className="ayurveda-header text-center mb-5">
        <h2 className="text-success">üåø Ayurvedic Wellness</h2>
        <p className="text-muted">
          Explore traditional remedies rooted in Kerala‚Äôs healing heritage.
        </p>
      </div>

      {loading ? (
        <div className="text-center text-muted">Loading Ayurvedic products...</div>
      ) : ayurvedicItems.length === 0 ? (
        <div className="alert alert-info text-center">
          No Ayurvedic medicines available at the moment.
        </div>
      ) : (
        <div className="row">
          {ayurvedicItems.map((item) => (
            <div key={item._id} className="col-md-4 mb-4">
              <div className="card ayurveda-card h-100 shadow-sm">
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title text-success">{item.name}</h5>
                  <p className="card-text flex-grow-1">{item.description || "Herbal remedy for wellness."}</p>
                  <p className="fw-bold">‚Çπ{item.price}</p>
                  <Link to={`/buy/${item._id}`} className="btn btn-outline-success mt-auto">
                    View & Buy
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
