import React, { useEffect } from "react";

function CategorySection() {
  const categories = [
    { name: "Medicines", image: "/images/medicines.jpg" },
    { name: "Personal Care", image: "/images/personalcare.jpg" },
    { name: "Health Care", image: "/images/healthcare.jpg" },
    { name: "Vitamins", image: "/images/vitamins.jpg" },
    { name: "Ayurveda", image: "/images/ayurveda.jpg" },
    { name: "Derma Care", image: "/images/dermacare.jpg" },
    { name: "Women", image: "/images/women.jpg" },
    { name: "Mom & Baby", image: "/images/momandbaby.jpg" },
  ];

  // Group categories into sets of 5 per slide
  const chunkedCategories = [];
  for (let i = 0; i < categories.length; i += 5) {
    chunkedCategories.push(categories.slice(i, i + 5));
  }

  useEffect(() => {
    const bootstrap = require("bootstrap/dist/js/bootstrap.bundle.min.js");
    const carouselElement = document.querySelector("#categoryCarousel");
    if (carouselElement) {
      new bootstrap.Carousel(carouselElement, {
        interval: 3000,
        ride: "carousel",
        pause: false,
        wrap: true,
      });
    }
  }, []);

  return (
    <section className="category-section container my-5">
      {/* âœ… Heading stays above the carousel */}
      <div className="text-center mb-4">
        <h2 className="fw-bold">Shop by Categories</h2>
      </div>

    </section>
  );
}

export default CategorySection;
