import React, { useEffect, useState } from "react";
import axios from "axios";

function MedicineSearch() {
  const [query, setQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [selectedMedicine, setSelectedMedicine] = useState(null);

  // Fetch suggestions dynamically
  useEffect(() => {
    if (query.length > 0) {
      axios
        .get("http://localhost:5000/api/medicines/search", {
          params: { q: query }, // single query param
        })
        .then((res) => {
          setSuggestions(res.data);
        })
        .catch((err) => console.error(err));
    } else {
      setSuggestions([]);
    }
  }, [query]);

  const handleSelect = (medicine) => {
    setSelectedMedicine(medicine);
    setQuery(medicine.name); // autofill input with name
    setSuggestions([]); // hide dropdown
  };

  return (
    <div
      style={{
        background: "#f8f9fa",
        padding: "20px",
        borderRadius: "8px",
        maxWidth: "600px",
        margin: "auto",
      }}
    >
      <h2 style={{ color: "#007bff" }}>ðŸ”Ž Search Medicines</h2>

      {/* Search Input */}
      <div style={{ position: "relative", marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Search by name, category, or symptom..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={{
            width: "100%",
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />

        {/* Suggestions Dropdown */}
        {suggestions.length > 0 && (
          <ul
            style={{
              position: "absolute",
              top: "40px",
              left: 0,
              right: 0,
              background: "white",
              border: "1px solid #ccc",
              borderRadius: "5px",
              maxHeight: "200px",
              overflowY: "auto",
              listStyle: "none",
              margin: 0,
              padding: 0,
              zIndex: 1000,
            }}
          >
            {suggestions.map((med) => (
              <li
                key={med._id}
                onClick={() => handleSelect(med)}
                style={{
                  padding: "10px",
                  cursor: "pointer",
                  borderBottom: "1px solid #eee",
                }}
                onMouseEnter={(e) =>
                  (e.currentTarget.style.backgroundColor = "#e6f0ff")
                }
                onMouseLeave={(e) =>
                  (e.currentTarget.style.backgroundColor = "white")
                }
              >
                <strong>{med.name}</strong> ({med.category}) â€“ for{" "}
                {med.symptoms.join(", ")}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Selected Medicine Details */}
      {selectedMedicine && (
        <div
          style={{
            padding: "15px",
            border: "1px solid #ddd",
            borderRadius: "5px",
            background: "white",
          }}
        >
          <h3>{selectedMedicine.name}</h3>
          <p>
            <strong>Category:</strong> {selectedMedicine.category}
          </p>
          <p>
            <strong>Symptoms:</strong> {selectedMedicine.symptoms.join(", ")}
          </p>
          <p>{selectedMedicine.description}</p>
          <p>
            <strong>Price:</strong> {selectedMedicine.price}â‚¹
          </p>
        </div>
      )}
    </div>
  );
}

export default MedicineSearch;
